INSERT INTO `news` (`idnews`, `title`, `content`, `image`, `categorynews`, `id`, `datepost`, `summary`) VALUES
(1, 'Người Nhật xếp hàng nhận cơm nắm sau thảm họa động đất', 'Hàng trăm người xếp hàng, có khi đến hơn một giờ, để nhận lương thực khi điện, nước cung cấp phục vụ hàng chục nghìn hộ gia đình vẫn bị cắt đứt sau các trận động đất mạnh ở miền nam Nhật Bản.', 'public/images/20160418114527n.jpg', 'W', 1, '2016-04-18', 'Hàng trăm người xếp hàng, có khi đến hơn một giờ, để nhận lương thực khi điện, nước cung cấp phục vụ hàng chục nghìn hộ gia đình vẫn bị cắt đứt sau các trận động đất mạnh ở miền nam Nhật Bản.'),
(2, 'Nga bị tố chặn phi cơ không quân Mỹ theo cách không an toàn', '<p>Vụ việc xảy ra ng&agrave;y 14/4. M&aacute;y bay trinh s&aacute;t Mỹ RC-135 đang bay theo lộ tr&igrave;nh thường lệ trong kh&ocirc;ng phận quốc tế tr&ecirc;n biển Baltic th&igrave; bị một phi cơ Su-27 của Nga chặn. RC-135 kh&ocirc;ng bay qua l&atilde;nh thổ Nga,&nbsp;<em>AFP</em>&nbsp;dẫn lời Laura Seal, người ph&aacute;t ng&ocirc;n Lầu Năm G&oacute;c, cho biết.</p>\r\n\r\n<p>&quot;Kiểu chặn đầu tr&ecirc;n kh&ocirc;ng thiếu chuy&ecirc;n nghiệp v&agrave; kh&ocirc;ng an to&agrave;n n&agrave;y c&oacute; thể g&acirc;y thương vong nghi&ecirc;m trọng đến tất cả phi c&ocirc;ng li&ecirc;n quan&quot;, b&agrave; Seal n&oacute;i. &quot;Quan trọng hơn, n&oacute; c&oacute; nguy cơ khiến căng thẳng giữa hai nước leo thang kh&ocirc;ng cần thiết&quot;.</p>\r\n\r\n<p>Vụ việc xảy ra kh&ocirc;ng l&acirc;u sau khi một m&aacute;y bay Su-24 Nga ng&agrave;y 12/4 bay chỉ 9 m ph&iacute;a tr&ecirc;n t&agrave;u khu trục Mỹ USS Donald Cook ở biển Baltic. C&aacute;ch phi cơ Nga s&agrave; xuống, bay ngay tr&ecirc;n boong t&agrave;u Mỹ giống như &quot;m&ocirc; phỏng tấn c&ocirc;ng&quot;, theo Bộ Chỉ huy ch&acirc;u &Acirc;u của qu&acirc;n đội Mỹ.</p>\r\n\r\n<p>Ng&agrave;y 11/4, hai m&aacute;y bay Su-24 bay qua USS Donald Cook khoảng 20 lần, đến gần t&agrave;u ở khoảng c&aacute;ch hơn 900 m, ở độ cao khoảng 30 m.</p>\r\n\r\n<p>Nga phủ nhận c&aacute;c h&agrave;nh động tr&ecirc;n l&agrave; khi&ecirc;u kh&iacute;ch hay liều lĩnh. Bộ Quốc ph&ograve;ng Nga khẳng định c&aacute;c m&aacute;y bay Su-24 của Nga bay gần USS Donald Cook đ&atilde; tu&acirc;n thủ tất cả quy định về an to&agrave;n.</p>\r\n', 'public/images/20160418114817nga.jpg', 'W', 1, '2016-04-18', 'Lầu Năm Góc hôm qua tố Nga chặn một phi cơ trinh sát của không quân Mỹ đang bay trên biển Baltic theo cách "thiếu chuyên nghiệp, không an toàn".'),
(3, 'Động đất mạnh rung chuyển Ecuador, 77 người chết', '<p>Trận động đất xảy ra v&agrave;o khoảng 23h58 GMT với t&acirc;m chấn c&aacute;ch thủ đ&ocirc; Quito, Ecuador khoảng 173 km, c&aacute;ch thị trấn ven biển Muisne chỉ 28 km về ph&iacute;a nam đ&ocirc;ng nam, theo Cơ quan Khảo s&aacute;t Địa chất Mỹ (USGS). USGS&nbsp;ước t&iacute;nh cường độ động đất l&agrave; 7,8 độ Richter c&ograve;n Ecuador v&agrave; c&aacute;c nước l&acirc;n cận đưa ra con số thấp hơn.</p>\r\n\r\n<p>Những cơn s&oacute;ng mạnh h&igrave;nh th&agrave;nh ngo&agrave;i khơi Ecuador trong b&aacute;n k&iacute;nh 300 km từ t&acirc;m chấn c&oacute; thể biến th&agrave;nh s&oacute;ng thần, đe dọa nước n&agrave;y v&agrave; Colombia,&nbsp;<em>Reuters&nbsp;</em>th&ocirc;ng tin từ Trung t&acirc;m Cảnh b&aacute;o S&oacute;ng thần Th&aacute;i B&igrave;nh Dương cho biết,</p>\r\n\r\n<p>&quot;T&ocirc;i đang ở nh&agrave; xem phim th&igrave; mọi thứ bắt đầu rung lắc. T&ocirc;i vội chạy ra ngo&agrave;i đường v&agrave; kh&ocirc;ng biết điều g&igrave; sẽ xảy ra&quot;, Lorena Cazares, nh&acirc;n vi&ecirc;n viễn th&ocirc;ng 36 tuổi ở Quito, n&oacute;i. Đường phố ở Quito tr&agrave;n ngập người, họ kh&ocirc;ng d&aacute;m ở trong nh&agrave; v&igrave; sợ bị v&ugrave;i lấp.</p>\r\n\r\n<p>H&igrave;nh ảnh do ch&iacute;nh phủ Ecuador cung cấp cho thấy một c&acirc;y cầu bị sập ở th&agrave;nh phố ven biển Guayaquil v&agrave; một t&ograve;a th&aacute;p bị sập tại s&acirc;n bay th&agrave;nh phố Manta. Quito bị mất điện nhưng sự cố đ&atilde; được khắc phục.</p>\r\n\r\n<p>&quot;Hiện đ&atilde; c&oacute; 77 người thiệt mạng&quot; v&agrave; gần 600 người bị thương,&nbsp;<em>AFP</em>&nbsp;dẫn lời Ph&oacute; tổng thống Ecuador Jorge Glas ph&aacute;t biểu trong một cuộc họp b&aacute;o. Ecuador ban bố t&igrave;nh trạng khẩn cấp quốc gia.</p>\r\n\r\n<p>Trung t&acirc;m Cảnh b&aacute;o S&oacute;ng thần Th&aacute;i B&igrave;nh Dương cho biết những cơn s&oacute;ng cao từ 0,3 đến 1 m c&oacute; thể sẽ ập v&agrave;o Ecuador. Peru đ&atilde; ban bố cảnh b&aacute;o s&oacute;ng thần sau động đất ngo&agrave;i khơi Ecuador.</p>\r\n', 'public/images/20160418122713thuonghai.jpg', 'W', 1, '2016-04-18', 'Một trận động đất mạnh 7,8 độ Richter xảy ra ở ven biển miền trung Ecuador làm ít nhất 77 người chết và có khả năng dẫn tới sóng thần.'),
(4, 'Nga công bố lương Tổng thống Putin năm 2015', '<p>Tổng thống Nga Vladimir Putin kiếm được 8,89 triệu r&uacute;p (khoảng 134.000 USD) trong năm 2015, cao hơn 1,24 triệu r&uacute;p so với năm 2014,<em>&nbsp;Tass</em>&nbsp;dẫn th&ocirc;ng tin từ tờ khai thuế đăng tải tr&ecirc;n website Điện Kremlin h&ocirc;m qua cho biết.</p>\r\n\r\n<p>&Ocirc;ng Putin sở hữu hai căn hộ, một gara rộng 18 m2 v&agrave; một khu đất rộng 1.500 m2. Tất cả t&agrave;i sản tr&ecirc;n đều ở Nga. &Ocirc;ng c&ograve;n c&oacute; hai xe Sedan cổ, một xe Niva v&agrave; một xe lều Skif.</p>\r\n\r\n<p>Thu nhập của Thủ tướng Nga Dmitry Medvedev năm 2015 tăng th&ecirc;m gần 9% l&ecirc;n 8,7 triệu r&uacute;p (hơn 131.000 USD), cao hơn một ch&uacute;t so với năm trước đ&oacute;.</p>\r\n\r\n<p>Thủ tướng Medvedev sở hữu căn hộ rộng 367 m2, cũng xuất hiện trong tờ khai thuế của vợ &ocirc;ng, c&ugrave;ng khu đất rộng 4.700 m2 với thời hạn thu&ecirc; l&agrave; 49 năm. &Ocirc;ng c&oacute; hai xe cổ l&agrave; 1948 GAZ-20 Pobeda v&agrave; 1962 GAZ-21 Volga. Vợ &ocirc;ng c&ograve;n l&agrave; chủ của hai b&atilde;i đỗ xe. Tất cả t&agrave;i sản của gia đ&igrave;nh Medvedev đều ở Nga.</p>\r\n\r\n<p>Tổng thống Putin năm ngo&aacute;i quyết định tự cắt giảm 10% lương của &ocirc;ng, Thủ tướng Medvedev v&agrave; một số quan chức cấp cao kh&aacute;c do t&igrave;nh kinh tế đất nước kh&oacute; khăn.&nbsp;<em>NBC News&nbsp;</em>cho biết thu nhập tổng thống Nga bằng đồng r&uacute;p tăng nhưng lại giảm 6.000 USD so với năm ngo&aacute;i cho thấy đồng r&uacute;p đ&atilde; bị suy yếu.</p>\r\n', 'public/images/20160418122934n.jpg', 'W', 1, '2016-04-18', 'Tổng thống Nga Vladimir Putin kiếm được khoảng 134.000 USD trong năm 2015, cao hơn so với năm trước đó.'),
(5, 'Ba Lan coi Nga là mối đe dọa lớn hơn IS', '<p>Trong bối cảnh hội nghị thượng đỉnh của Tổ chức Hiệp ước Bắc Đại T&acirc;y Dương (NATO) sẽ diễn ra v&agrave;o th&aacute;ng 7 tới đ&acirc;y, Ba Lan đang t&iacute;ch cực th&uacute;c giục để NATO tăng cường điều động binh sĩ đến những v&ugrave;ng bi&ecirc;n giới của nước n&agrave;y. Ph&aacute;t biểu trước một hội nghị về an ninh diễn ra h&ocirc;m qua ở thủ đ&ocirc; Bratislava,&nbsp;Slovakia, Ngoại trưởng Ba Lan Witold Waszczykowski đ&atilde; nhắc tới Nga như một &quot;mối đe dọa hiện hữu&quot;, theo&nbsp;<em>Sputnik</em>.</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta c&oacute; những mối đe dọa hiện hữu v&agrave; kh&ocirc;ng hiện hữu. Tất nhi&ecirc;n, h&agrave;nh động của người Nga l&agrave; một mối đe dọa hiện hữu bởi ch&uacute;ng c&oacute; thể ti&ecirc;u diệt nhiều quốc gia&quot;, &ocirc;ng&nbsp;Waszczykowski n&oacute;i.</p>\r\n\r\n<p>Theo &ocirc;ng, c&aacute;c tổ chức khủng bố hay l&agrave;n s&oacute;ng di cư khổng lồ đang tr&agrave;n v&agrave;o ch&acirc;u &Acirc;u l&agrave; những &quot;mối đe dọa kh&ocirc;ng hiện hữu&quot;.&nbsp;Khi được hỏi về phiến qu&acirc;n Nh&agrave; nước Hồi gi&aacute;o (IS), &ocirc;ng Waszczykowski tuy&ecirc;n bố Moscow mang tới mối đe dọa lớn hơn cả tổ chức n&agrave;y.</p>\r\n\r\n<p>IS &quot;l&agrave; một hiểm họa nghi&ecirc;m trọng nhưng kh&ocirc;ng phải mối đe dọa hiện hữu với ch&acirc;u &Acirc;u&quot;, &ocirc;ng Waszczykowski n&oacute;i v&agrave; nhấn mạnh rằng Ba Lan rất mong muốn sự hiện diện qu&acirc;n sự của c&aacute;c th&agrave;nh vi&ecirc;n NATO trong khu vực, như l&agrave; một &quot;biểu tượng cho thấy quyết t&acirc;m&quot; của khối nhằm &quot;bảo vệ sườn ph&iacute;a đ&ocirc;ng&quot;.</p>\r\n\r\n<p>Lầu Năm G&oacute;c th&aacute;ng trước cũng th&ocirc;ng b&aacute;o sẽ triển khai lu&acirc;n phi&ecirc;n th&ecirc;m một lữ đo&agrave;n thiết gi&aacute;p chiến đấu tới ch&acirc;u &Acirc;u như một phần trong nỗ lực đối ph&oacute; với c&aacute;i m&agrave; Mỹ gọi l&agrave; h&agrave;nh vi g&acirc;y hấn của Nga ở ch&acirc;u lục n&agrave;y. Lữ đo&agrave;n thiết gi&aacute;p n&oacute;i tr&ecirc;n dự kiến đi v&agrave;o hoạt động từ th&aacute;ng 2/2017, sẽ phối hợp tiến h&agrave;nh c&aacute;c cuộc tập trận chung với&nbsp;Estonia,&nbsp;Latvia,&nbsp;Lithuania, Ba Lan,&nbsp;Romania,&nbsp;Bulgaria v&agrave;&nbsp;Hungary.</p>\r\n\r\n<p>Quan hệ Nga - phương T&acirc;y trở n&ecirc;n xấu đi kể từ khi nước n&agrave;y s&aacute;p nhập b&aacute;n đảo Crimea v&agrave;o l&atilde;nh thổ. Mỹ v&agrave; một số nước ch&acirc;u &Acirc;u b&ecirc;n cạnh đ&oacute; c&ograve;n c&aacute;o buộc Nga can thiệp qu&acirc;n sự v&agrave;o Ukraine khiến khủng hoảng ở quốc gia l&aacute;ng giềng c&agrave;ng trầm trọng hơn. Tuy nhi&ecirc;n, Moscow phủ nhận mọi lời buộc tội.</p>\r\n\r\n<p>Bộ trưởng Quốc ph&ograve;ng Mỹ Ashton Carter hồi th&aacute;ng hai c&ocirc;ng bố ng&acirc;n s&aacute;ch đề xuất của Lầu Năm G&oacute;c cho năm t&agrave;i kh&oacute;a tới, trong đ&oacute;, số tiền chi cho c&aacute;c hoạt động qu&acirc;n sự ở ch&acirc;u &Acirc;u tăng 4 lần so với năm ngo&aacute;i, l&ecirc;n mức 3,4 tỷ USD.</p>\r\n', 'public/images/20160418123127balan.jpeg', 'W', 1, '2016-04-18', 'Ngoại trưởng Ba Lan hôm qua so sánh Nga là một mối đe dọa còn lớn hơn cả tổ chức Nhà nước Hồi giáo, đồng thời thúc giục NATO tăng cường hiện diện tại các khu vực biên giới nước này.'),
(6, 'Điện Kremlin xin lỗi vì đưa thông tin sai về Hồ sơ Panama', '<p>Trong buổi trả lời trực tiếp c&acirc;u hỏi của người d&acirc;n được ph&aacute;t s&oacute;ng tr&ecirc;n truyền h&igrave;nh h&ocirc;m qua, Tổng thống Nga Vladimir Putin nhắc lại th&ocirc;ng tin sai lầm n&agrave;y trong c&aacute;c b&igrave;nh luận m&agrave; &ocirc;ng đưa ra li&ecirc;n quan đến việc &ocirc;ng nghĩ ai l&agrave; người đứng sau vụ r&ograve; rỉ t&agrave;i liệu lớn nhất lịch sử Hồ sơ Panama.</p>\r\n\r\n<p>Tổng thống Putin chỉ tr&iacute;ch Mỹ l&agrave; nước đứng sau vụ việc. Theo &ocirc;ng, Nga c&agrave;ng tiến gần đến bầu cử quốc hội th&igrave; c&agrave;ng c&oacute; th&ecirc;m c&aacute;c vụ r&ograve; rỉ tương tự. Những t&agrave;i liệu dường như do luật sư chuẩn bị chứ kh&ocirc;ng phải l&agrave; c&aacute;c nh&agrave; b&aacute;o.</p>\r\n\r\n<p>&quot;Đ&acirc;y đ&uacute;ng hơn l&agrave; lỗi của b&ecirc;n sắp xếp t&agrave;i liệu cho buổi l&agrave;m việc, lỗi của t&ocirc;i&quot;,&nbsp;<em>Reuters&nbsp;</em>dẫn lời người ph&aacute;t ng&ocirc;n Điện Kremlin&nbsp;Dmitry&nbsp;Peskov n&oacute;i với c&aacute;c ph&oacute;ng vi&ecirc;n. &quot;Một số th&ocirc;ng tin kh&ocirc;ng được kiểm tra, r&agrave; so&aacute;t lại v&agrave; ch&uacute;ng t&ocirc;i đ&atilde; đưa ch&uacute;ng cho ng&agrave;i tổng thống. Ch&uacute;ng t&ocirc;i đ&atilde; xin lỗi ng&acirc;n h&agrave;ng v&agrave; sẽ gửi lời xin lỗi đến tờ b&aacute;o&quot;.</p>\r\n\r\n<p>Hồ sơ Panama l&agrave; t&ecirc;n gọi ngắn gọn d&ugrave;ng để chỉ số t&agrave;i liệu của Mossack Fonseca, một c&ocirc;ng ty luật tại Panama, được h&eacute; lộ hồi đầu th&aacute;ng, phơi b&agrave;y một mạng lưới c&ocirc;ng ty &quot;ma&quot; khổng lồ tr&ecirc;n thế giới, lập ra để gi&uacute;p người gi&agrave;u trốn thuế v&agrave; trong một số trường hợp l&agrave; rửa tiền.</p>\r\n', 'public/images/20160418123506putin.jpg', 'W', 1, '2016-04-18', 'Điện Kremlin hôm nay xin lỗi ngân hàng đầu tư Goldman Sachs và tờ Suddeutsche Zeitung của Đức về việc một số cố vẫn đã thông báo nhầm cho Tổng thống Nga rằng nhà băng Mỹ sở hữu báo trên.'),
(7, 'Bị phạt vì mua chui cổ phiếu Nhựa Tân Tiến', '<p>Ủy ban Chứng kho&aacute;n Nh&agrave; nước (UBCKNN) vừa ra quyết định xử phạt &ocirc;ng Nguyễn Tấn Bảo (quận Ph&uacute; Nhuận, TP HCM) v&agrave; &ocirc;ng Ho&agrave;ng Ch&iacute; Thanh (quận B&igrave;nh Thạnh, TP HCM). Mỗi c&aacute; nh&acirc;n bị phạt 62,5 triệu đồng.&nbsp;</p>\r\n\r\n<p>Theo UBCKNN, &ocirc;ng Thanh v&agrave; &ocirc;ng Bảo c&ugrave;ng mở tại C&ocirc;ng ty chứng kho&aacute;n Bản Việt v&agrave; đ&atilde; mua v&agrave;o 730.000 cổ phiếu TTP của C&ocirc;ng ty cổ phần Bao b&igrave; Nhựa T&acirc;n Tiến. Sau giao dịch, c&aacute;c c&aacute; nh&acirc;n n&agrave;y trở th&agrave;nh cổ đ&ocirc;ng lớn của Nhựa T&acirc;n Tiến khi n&acirc;ng tỷ lệ sở hữu sau giao dịch l&ecirc;n 5,4%. Giao dịch diễn ra v&agrave;o ng&agrave;y 31/8/2015 nhưng đến ng&agrave;y 5/10/2015, c&aacute;c c&aacute; nh&acirc;n n&agrave;y mới gửi b&aacute;o c&aacute;o về Sở giao dịch Chứng kho&aacute;n TP HCM.&nbsp;</p>\r\n\r\n<p>Cổ phiếu TTP hủy ni&ecirc;m yết tự nguyện từ ng&agrave;y 15/10/2015 với l&yacute; do được l&atilde;nh đạo doanh nghiệp đưa ra l&agrave; để&nbsp;tập trung việc t&aacute;i cơ cấu.</p>\r\n', 'public/images/20160418124557kd.jpg', 'B', 1, '2016-04-18', 'Ông Nguyễn Tấn Bảo và ông Hoàng Chí Thanh chậm báo cáo khi mua vào 730.000 cổ phiếu và trở thành cổ đông lớn của Nhựa Tân Tiến.'),
(8, '137 dự án ''chết lâm sàng'' tại TP HCM trước cơ hội M&A', '<p>B&aacute;o c&aacute;o t&igrave;nh h&igrave;nh thị trường bất động sản của Hiệp hội Bất động sản TP HCM (HoREA) cho biết năm 2015, c&aacute;c doanh nghiệp tại TP HCM đ&atilde; b&aacute;n được 26.500 căn trong tổng số 50.000 căn ch&agrave;o b&aacute;n. Con số n&agrave;y tăng khoảng 10.000 căn so với năm 2014.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, đến qu&yacute; I/2016, giao dịch c&oacute; dấu hiệu chững lại so với qu&yacute; IV/2015 khi mới b&aacute;n được khoảng 9.000 căn trong tổng số khoảng 57.000 căn dự kiến ch&agrave;o b&aacute;n trong năm 2016. Trong đ&oacute;, khoảng 700 người nước ngo&agrave;i mua căn hộ cao cấp.&nbsp;</p>\r\n\r\n<p>HoREA cũng cho biết, ph&acirc;n kh&uacute;c bất động sản cao cấp c&oacute; sự tăng trưởng mạnh nhất, tập trung v&agrave;o khu trung t&acirc;m, khu Đ&ocirc;ng v&agrave; khu Nam th&agrave;nh phố. Ph&acirc;n kh&uacute;c văn ph&ograve;ng cho thu&ecirc;, bất động sản thương mại tăng trưởng mạnh.</p>\r\n\r\n<p>HoREA dự b&aacute;o, c&oacute; thể dự b&aacute;o thị trường bất động sản năm 2016 sẽ tiếp tục tăng trưởng kh&ocirc;ng thấp hơn năm 2015, v&agrave; sẽ c&oacute; sự chuyển hướng t&iacute;ch cực, đầu tư nhiều hơn v&agrave;o ph&acirc;n kh&uacute;c nh&agrave; ở x&atilde; hội, nh&agrave; ở thương mại quy m&ocirc; vừa v&agrave; nhỏ, c&oacute; gi&aacute; b&aacute;n vừa t&uacute;i tiền, cũng như sự gia tăng đầu tư v&agrave;o bất động sản c&ocirc;ng nghiệp, văn ph&ograve;ng cho thu&ecirc;, căn hộ dịch vụ trong giai đoạn đất nước hội nhập s&acirc;u v&agrave;o nền kinh tế thế giới.&nbsp;</p>\r\n\r\n<p>T&igrave;nh h&igrave;nh mua b&aacute;n, chuyển nhượng dự &aacute;n theo HoREA cũng diễn ra kh&aacute; s&ocirc;i động. B&ecirc;n cạnh nhiều trường hợp mua b&aacute;n chuyển nhượng dự &aacute;n dưới h&igrave;nh thức mua b&aacute;n cổ phần, chuyển nhượng c&ocirc;ng ty, UBND th&agrave;nh phố đ&atilde; chấp thuận cho chuyển nhượng 23 dự &aacute;n, tăng 2,55 lần so với năm 2014. Đến qu&yacute; I/2016 c&oacute; th&ecirc;m 5 dự &aacute;n xin chuyển nhượng.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo HoREA, thị trường bất động sản tại TP HCM đang tiềm ẩn những yếu tố bất ổn. Năm 2015, thị trường bất động sản đ&atilde; c&oacute; dấu hiệu ph&aacute;t triển n&oacute;ng ở ph&acirc;n kh&uacute;c bất động sản cao cấp khi số lượng nh&agrave; đầu tư kinh doanh thứ cấp (mua đi b&aacute;n lại) cũng tăng gấp 3 lần, chiếm khoảng 15% giao dịch. C&ugrave;ng với xu hướng đ&oacute;, gi&aacute; b&aacute;n bất động sản cao cấp tại nhiều dự &aacute;n cũng tăng từ 5% đến 15% so với năm 2014.&nbsp;</p>\r\n\r\n<p>To&agrave;n th&agrave;nh phố hiện c&oacute; 1.219 dự &aacute;n với hơn 315.000 căn nh&agrave;. Trong đ&oacute;, c&oacute; 549 dự &aacute;n đ&atilde; ho&agrave;n th&agrave;nh (chiếm 45%) với hơn 78.000 căn, 584 dự &aacute;n đang triển khai đầu tư (chiếm 48%) v&agrave; c&oacute; đến 86 dự &aacute;n hết hạn c&ocirc;ng nhận chủ đầu tư (chiếm 7%).&nbsp;</p>\r\n\r\n<p>C&oacute; đến 137 dự &aacute;n tạm ngưng thi c&ocirc;ng hoặc hết hạn c&ocirc;ng nhận chủ đầu tư, chiếm 11,2% tổng số dự &aacute;n. HoREA cũng nhận định đ&acirc;y cũng l&agrave; nguồn h&agrave;ng h&oacute;a dự &aacute;n tiềm năng cho thị trường mua b&aacute;n, chuyển nhượng dự &aacute;n (M&amp;A) nếu c&oacute; ch&iacute;nh s&aacute;ch v&agrave; cơ chế ph&ugrave; hợp. &nbsp;Ngo&agrave;i ra, tr&ecirc;n địa b&agrave;n TP HCM cũng c&oacute; 52 dự &aacute;n chưa thể triển khai được do vướng kh&acirc;u giải ph&oacute;ng mặt bằng.&nbsp;</p>\r\n\r\n<p>&ldquo;Đ&acirc;y cũng l&agrave; phần ch&igrave;m của tảng băng h&agrave;ng tồn kho tr&ecirc;n thị trường bất động sản cần phải c&oacute; giải ph&aacute;p hợp l&yacute;, n&ecirc;n rất cần c&oacute; cơ chế hỗ trợ doanh nghiệp để sớm triển khai dự &aacute;n v&agrave; vừa đảm bảo lợi &iacute;ch của người c&oacute; đất, vừa đảm bảo lợi &iacute;ch chung của x&atilde; hội&rdquo;, b&aacute;o c&aacute;o đ&aacute;nh gi&aacute;.&nbsp;</p>\r\n', 'public/images/20160418124734135.jpg', 'B', 1, '2016-04-18', 'Các dự án tạm ngưng thi công hoặc hết hạn công nhận chủ đầu tư tại TP HCM được đánh giá là nguồn hàng hóa dự án tiềm năng cho thị trường mua bán, chuyển nhượng (M&A).'),
(9, 'Lo ngại bẫy giá cá tra ảo từ thương lái Trung Quốc', '<p>Hiện gi&aacute; c&aacute; tra nguy&ecirc;n liệu tại c&aacute;c tỉnh An Giang, Đồng Th&aacute;p, Cần Thơ được ghi nhận đ&atilde; tăng mạnh từ khoảng 19.000 đồng/kg l&ecirc;n 22.500 đồng/kg nhưng vẫn kh&ocirc;ng đủ cung cấp. Nguy&ecirc;n nh&acirc;n được x&aacute;c định l&agrave; do thương l&aacute;i Trung Quốc tập trung thu mua mạnh. Điều n&agrave;y đang khiến c&aacute;c nh&agrave; m&aacute;y tại khu vực Đồng bằng S&ocirc;ng Cửu Long gặp kh&oacute; khăn trong việc thu mua nguy&ecirc;n liệu phục vụ chế biến xuất khẩu.</p>\r\n\r\n<p>Gi&aacute;m đốc Sở C&ocirc;ng Thương th&agrave;nh phố Cần Thơ - Nguyễn Minh Toại cho rằng, việc gi&aacute; c&aacute; tra tăng mạnh l&agrave; t&iacute;n hiệu vui v&agrave; l&agrave; cơ hội tốt cho người nu&ocirc;i trồng thủy sản. H&igrave;nh thức mua c&aacute; tra của thương l&aacute;i Trung Quốc l&agrave; xẻ c&aacute; ra cắt kh&uacute;c hoặc để nguy&ecirc;n con để b&aacute;n. Nếu trường hợp n&agrave;o b&aacute;n đạt được y&ecirc;u cầu ti&ecirc;u thụ h&agrave;ng h&oacute;a cho b&agrave; con n&ocirc;ng d&acirc;n, người d&acirc;n đảm bảo c&oacute; hiệu quả th&igrave; Sở ủng hộ. Tuy nhi&ecirc;n, đơn vị sẽ tập trung gi&aacute;m s&aacute;t việc mua b&aacute;n c&oacute; yếu tố nước ngo&agrave;i.</p>\r\n\r\n<p>Theo Hiệp hội Chế biến v&agrave; Xuất khẩu Thủy sản Việt Nam (VASEP), gi&aacute; c&aacute; tra tăng mạnh l&agrave; t&iacute;n hiệu t&iacute;ch cực. Song hiệp hội cũng khuyến c&aacute;o người nu&ocirc;i c&aacute; v&agrave; c&aacute;c c&ocirc;ng ty chế biến xuất khẩu cẩn thận để tr&aacute;nh bẫy &ldquo;gi&aacute; ảo&rdquo; khi thương l&aacute;i Trung Quốc thu mua với gi&aacute; cao.</p>\r\n', 'public/images/20160418010406tq.jpg', 'B', 1, '2016-04-18', 'Giá cá tra nguyên liệu tại các tỉnh An Giang, Đồng Tháp, Cần Thơ... có xu hướng tăng mạnh do thương lái Trung Quốc tập trung thu mua, song theo các chuyên gia đây có thể chỉ là bẫy giá ảo.'),
(10, 'Apple đặt hàng Samsung 100 triệu màn hình iPhone đời mới', '<p>Theo một b&agrave;i viết từ&nbsp;<em>Wall Street Journal China</em>, Apple vừa k&yacute; một hợp đồng với Samsung trong ba năm để đặt h&agrave;ng h&atilde;ng H&agrave;n Quốc chế tạo m&agrave;n h&igrave;nh cho những chiếc iPhone thế hệ mới sắp ra mắt. Số lượng sản phẩm cung cấp được cho l&ecirc;n tới 100 triệu chiếc v&agrave; h&atilde;ng H&agrave;n Quốc đ&atilde; bắt đầu tăng sản lượng sản xuất m&agrave;n h&igrave;nh OLED l&ecirc;n tới gấp 8 lần từ giữa năm nay.</p>\r\n\r\n<p>Thay v&igrave; sử dụng m&agrave;n h&igrave;nh LCD IPS như từ trước đến giờ, iPhone thế hệ mới của Apple được cho sẽ sử dụng m&agrave;n h&igrave;nh OLED nhằm c&oacute; được thiết kế mỏng hơn. Trước đ&oacute;, nhiều tin đồn cũng cho hay Apple chuẩn bị chuyền sang c&ocirc;ng nghệ m&agrave;n h&igrave;nh OLED. M&agrave;n h&igrave;nh OLED đang c&oacute; chi ph&iacute; sản xuất ng&agrave;y c&agrave;ng rẻ so với LCD.&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, nguồn tin tr&ecirc;n cũng cho rằng phải tới 2017 Apple mới chuyển sang sử dụng m&agrave;n h&igrave;nh OLED tr&ecirc;n iPhone, tức tới iPhone 7s thay v&igrave; iPhone 7 chuẩn bị ra mắt trong năm nay.&nbsp;</p>\r\n', 'public/images/20160418012101app.jpg', 'T', 1, '2016-04-18', 'Những thế hệ iPhone ra mắt vào 2017 được cho sẽ chuyển sang sử dụng màn hình OLED do Samsung sản xuất.'),
(11, '10 điểm nâng cấp ở HTC 10', '<p><strong>1. M&agrave;n h&igrave;nh Quad HD</strong></p>\r\n\r\n<p>Kh&ocirc;ng chỉ tăng k&iacute;ch thước m&agrave;n h&igrave;nh l&ecirc;n 0,2 inch so với M9, HTC 10 c&ograve;n được n&acirc;ng cấp độ ph&acirc;n giải l&ecirc;n tới 2.560 x 1.440 pixel (M9 chỉ c&oacute; độ ph&acirc;n giải 1.920 x 1080 pixel). Mặc d&ugrave; từng xuất hiện tr&ecirc;n One M9+, E9+, ME nhưng đ&acirc;y l&agrave; lần đầu ti&ecirc;n HTC t&iacute;ch hợp độ ph&acirc;n giải m&agrave;n h&igrave;nh cao đến vậy cho một sản phẩm cao cấp.</p>\r\n\r\n<p>Ngo&agrave;i ra, theo HTC, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n cho độ nhạy hơn tới 50% so với &ldquo;đ&agrave;n anh&rdquo; M9.</p>\r\n\r\n<p><strong>2. Tỷ lệ m&agrave;n h&igrave;nh so với m&aacute;y</strong></p>\r\n\r\n<p>Với việc chiếm tới gần 71,1% tỷ lệ tổng thể của m&aacute;y, cộng th&ecirc;m viền bezel mỏng hơn, m&agrave;n h&igrave;nh HTC 10 cho cảm gi&aacute;c trải nghiệm tốt hơn b&ecirc;n trong k&iacute;ch thước kh&aacute; nhỏ gọn.</p>\r\n\r\n<p>Theo&nbsp;<em>Phonearena</em>, d&ugrave; HTC 10 kh&ocirc;ng phải l&agrave; smartphone 5,2 inch nhỏ gọn nhất, cũng kh&ocirc;ng phải l&agrave; thiết bị mỏng nhất nhưng kh&ocirc;ng thể phủ nhận chiếc điện thoại n&agrave;y c&oacute; tỷ lệ m&agrave;n h&igrave;nh so với m&aacute;y ho&agrave;n thiện nhất hiện nay.</p>\r\n\r\n<p><strong>3. Camera 12 megapixel với khẩu độ lớn</strong></p>\r\n\r\n<p>Khả năng chụp ảnh của HTC 10 đ&atilde; rất ấn tượng nhờ c&aacute;c th&ocirc;ng số mạnh như cảm biến 12 megapixel c&ocirc;ng nghệ UltraPixel (mỗi UltraPixel c&oacute; k&iacute;ch thước 1,55um), khẩu độ f/1.8 cho ph&eacute;p nhận th&ecirc;m 136% &aacute;nh s&aacute;ng tr&ecirc;n h&igrave;nh, chống rung quang học (OIS), tự động lấy n&eacute;t bằng tia lazer, chụp ảnh RAW, quay video 4K&hellip;</p>\r\n\r\n<p><strong>4. Khả năng &#39;tự sướng&#39;</strong></p>\r\n\r\n<p>Kh&ocirc;ng chỉ m&aacute;y ảnh ch&iacute;nh, m&aacute;y ảnh phụ của HTC 10 cũng c&oacute; khả năng chụp ảnh mạnh mẽ với cảm biến 5 megapixel (k&iacute;ch thước mỗi pixel l&agrave; 1,34um), OIS, khẩu độ f/1.8&hellip; Đặc biệt, t&iacute;nh năng UltraSelfie&nbsp;hứa hẹn sẽ biến flagship của HTC trở th&agrave;nh một trong những thiết bị &ldquo;tự sướng&rdquo; tốt nhất hiện nay.</p>\r\n\r\n<p><strong>5. Phần cứng mạnh mẽ</strong></p>\r\n\r\n<p>HTC 10 đang l&agrave; một trong những smartphone mạnh nhất hiện nay với phần cứng vi xử l&yacute; l&otilde;i tứ Snapdragon 820, RAM 4GB, chip đồ họa Adreno 530&hellip; Đ&acirc;y l&agrave; c&aacute;c th&ocirc;ng số đ&atilde; c&oacute; mặt tr&ecirc;n bộ đ&ocirc;i Galaxy S7/S7 edge của Samsung hay G5 của LG c&aacute;ch đ&acirc;y &iacute;t l&acirc;u.</p>\r\n\r\n<p><strong>6. Loa BoomSound được cải thiện</strong></p>\r\n\r\n<p>Từng c&oacute; mặt tr&ecirc;n nhiều thiết bị cả cao cấp lẫn tầm trung, tuy nhi&ecirc;n hệ thống BoomSound tr&ecirc;n HTC 10 đ&atilde; được cải thiện nhờ t&iacute;ch hợp bộ giải m&atilde; &acirc;m thanh (DAC) mới k&egrave;m một bộ khuếch đại &acirc;m thanh tr&ecirc;n mỗi loa. M&aacute;y c&oacute; khả năng ph&aacute;t nhạc chất lượng cao 24-bit, khả năng t&aacute;i tạo &acirc;m thanh mang lại trải nghiệm tốt hơn cho người d&ugrave;ng. Đ&acirc;y l&agrave; smartphone đầu ti&ecirc;n tr&ecirc;n thế giới sở hữu t&iacute;nh năng n&agrave;y.</p>\r\n\r\n<p><strong>7. Pin 3.000 mAh v&agrave; sạc nhanh Quick Charge 3.0</strong></p>\r\n\r\n<p>Với dung lượng l&ecirc;n tới 3.000 mAh, HTC 10 đ&atilde; được n&acirc;ng cấp so với One M9 (2.840 mAh). Tuy nhi&ecirc;n, h&atilde;ng điện tử Đ&agrave;i Loan c&ograve;n t&iacute;ch hợp c&aacute;c c&ocirc;ng nghệ tiết kiệm pin cho thời gian sử dụng l&acirc;u hơn, như PowerBotics&nbsp;cho ph&eacute;p tối ưu hiệu năng (cả phần cứng lẫn phần mềm), tăng thời lượng sử dụng pin l&ecirc;n đến 30%. Đặc biệt, khả năng sạc nhanh Quick Charge 3.0 gi&uacute;p thiết bị kh&ocirc;ng lo hết pin, bởi chỉ cần 30 ph&uacute;t, m&aacute;y đ&atilde; nạp từ 0% l&ecirc;n 50%.</p>\r\n\r\n<p><strong>8. Giao diện Sense 8</strong></p>\r\n\r\n<p>Tr&ecirc;n nền tảng Android 6.0 Marshmallow mới nhất, Sense UI 8 của HTC 10 đ&atilde; được HTC cải tiến, nổi bật nhất l&agrave; giao diện Freestyle mới tiện lợi hơn, cho ph&eacute;p người d&ugrave;ng k&eacute;o thả ứng dụng dễ d&agrave;ng, sắp xếp widget khoa học v&agrave; c&oacute; thể t&ugrave;y biến dễ d&agrave;ng. Tất nhi&ecirc;n, giao diện n&agrave;y cũng được tinh giản hơn, dễ l&agrave;m quen hơn v&agrave; cũng &iacute;t &ldquo;ngốn&rdquo; t&agrave;i nguy&ecirc;n hơn.</p>\r\n\r\n<p><strong>9. USB Type-C</strong></p>\r\n\r\n<p>Gần đ&acirc;y, giới c&ocirc;ng nghệ nhận định USB Type-C sẽ l&agrave; chuẩn kết nối của tương lai v&agrave; HTC đ&atilde; kh&ocirc;ng bỏ qua điều đ&oacute; khi t&iacute;ch hợp n&oacute; cho HTC 10. USB Type-C hiện sở hữu tốc độ truyền tải dữ liệu l&ecirc;n tới 10 Gb/gi&acirc;y, gấp đ&ocirc;i chuẩn 3.0 v&agrave; tương đương với chuẩn Thunderbolt.</p>\r\n\r\n<p><strong>10. M&aacute;y qu&eacute;t v&acirc;n tay</strong></p>\r\n\r\n<p>C&ugrave;ng với USB Type-C, HTC cũng đ&atilde; đưa v&agrave;o cảm biến v&acirc;n tay được t&iacute;ch hợp ngay trong ph&iacute;m Home. Được biết, tốc độ nhận diện của n&oacute; l&agrave; 0,2 gi&acirc;y, cao hơn so với nhiều smartphone kh&aacute;c hiện nay như Nexus 6P, Galaxy S7, G5 v&agrave; cả iPhone 6s.</p>\r\n', 'public/images/20160418012906dt.jpg', 'T', 1, '2016-04-18', 'Smartphone có tên gọi khá đơn giản, HTC 10 có hàng loạt tính năng nâng cấp so với thế hệ đàn anh trước đó.'),
(12, 'Màn hình 27 inch viền siêu mỏng giá 5,3 triệu đồng', '<p>M&agrave;n h&igrave;nh m&aacute;y t&iacute;nh k&iacute;ch thước 27 inch đang dần phổ biến tr&ecirc;n thị trường, mang đến cho người d&ugrave;ng kh&ocirc;ng gian trải nghiệm rộng r&atilde;i nhưng kh&ocirc;ng qu&aacute; cồng kềnh. Sản phẩm VX2771 của ViewSonic được ph&aacute;t triển nhằm đ&aacute;p ứng nhu cầu đ&oacute;.</p>\r\n\r\n<p>Điểm nổi bật tr&ecirc;n model n&agrave;y l&agrave; phần viền được l&agrave;m mỏng chỉ 3,8 mm, tạo cảm gi&aacute;c h&igrave;nh ảnh tr&agrave;n s&aacute;t cạnh. Với tấm nền PLS, ViewSonic VX2771 cho g&oacute;c nh&igrave;n kh&aacute; rộng v&agrave; h&igrave;nh ảnh đồng nhất tại c&aacute;c vị tr&iacute; xem kh&aacute;c nhau.</p>\r\n\r\n<p>VX2771 c&oacute; độ ph&acirc;n giải Full HD, được trang bị c&ocirc;ng nghệ Flicker Free gi&uacute;p loại bỏ t&igrave;nh trạng nhấp nh&aacute;y m&agrave;n h&igrave;nh n&ecirc;n hạn chế t&igrave;nh trạng mỏi mắt khi người d&ugrave;ng l&agrave;m việc, chơi game hay xem phim trong thời gian d&agrave;i. N&oacute; cũng t&iacute;ch hợp c&ocirc;ng nghệ&nbsp;Blue Light Filter để lọc bớt tia s&aacute;ng kh&ocirc;ng c&oacute; lợi cho v&otilde;ng mạc.</p>\r\n\r\n<p>Ch&acirc;n đế m&agrave;n h&igrave;nh c&oacute; thiết kế hiện đại, g&oacute;c nghi&ecirc;ng linh hoạt v&agrave; được l&agrave;m phay xước kh&aacute; sang trọng. ViewSonic VX2771 c&oacute; gi&aacute; 5,3 triệu đồng với hai lựa chọn ch&acirc;n đế m&agrave;u bạc hoặc m&agrave;u v&agrave;ng, nhắm đến đối tương văn ph&ograve;ng v&agrave; giải tr&iacute; đa phương tiện.</p>\r\n\r\n<p>Sản phẩm kh&aacute;c được ViewSonic giới thiệu l&agrave; mẫu VX2457 d&agrave;nh cho những người chuy&ecirc;n game. Với thời gian đ&aacute;p ứng 1 ms c&ugrave;ng tần số qu&eacute;t 75 Hz, m&agrave;n h&igrave;nh hạn chế t&igrave;nh trạng &quot;lag&quot; khi chơi tr&ograve; chơi.</p>\r\n\r\n<p>VX2457 sở hữu tấm nền 24 inch độ ph&acirc;n giải Full HD, b&ecirc;n trong một thiết kế kh&aacute; cổ điển với viền m&agrave;n h&igrave;nh tương đối d&agrave;y. B&ugrave; lại, sản phẩm được trang bị khả năng đồng bộ VESA-Sync v&agrave; c&ocirc;ng nghệ FreeSync của AMD gi&uacute;p loại bỏ hiện tượng nh&ograve;e h&igrave;nh khi chơi c&aacute;c game h&agrave;nh động chuyển động nhanh, đặc biệt l&agrave; c&aacute;c tựa game bắn s&uacute;ng g&oacute;c nh&igrave;n người thứ nhất</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, n&oacute; c&oacute; c&aacute;c chế độ tối ưu cho Văn bản, Tr&ograve; chơi, Phim, Web v&agrave; Mono ph&ugrave; hợp cho c&aacute;c trường hợp sử dụng kh&aacute;c nhau. M&agrave;n h&igrave;nh sở hữu ch&acirc;n đế tr&ograve;n được l&agrave;m to bản, mặt sau thiết kế c&oacute; thể treo tường.</p>\r\n\r\n<p>VX2457 sở hữu kết nối HDMI, DisplayPort v&agrave; VGA. Sản phẩm cũng t&iacute;ch hợp th&ecirc;m loa ngay tr&ecirc;n m&agrave;n h&igrave;nh. ViewSonic VX2457 c&oacute; gi&aacute; khoảng 3,5 triệu đồng.</p>\r\n', 'public/images/20160418013337mh.jpg', 'T', 1, '2016-04-18', 'ViewSonic VX2771 viền mỏng chỉ 3,8 mm, tấm nền PLS cho góc nhìn rộng, độ phân giải Full HD.'),
(13, 'Gionee M5 mini - smartphone giá rẻ pin lâu', '<p>Trong khi những đối thủ gi&aacute; 3 triệu đồng kh&aacute;c như Zenfone Go hay Galaxy J2 c&oacute; pin chỉ khoảng 2.000 mAh, Gionee M5 mini sở hữu pin dung lượng tới 4.000 mAh. Nhờ thế c&ugrave;ng mức độ sử dụng, m&aacute;y c&oacute; thể hoạt động được 2 đến 3 ng&agrave;y, gấp đ&ocirc;i thời gian sử dụng trung b&igrave;nh của phần lớn c&aacute;c smartphone Android hiện giờ.&nbsp;</p>\r\n\r\n<p>Thử nghiệm bằng c&ocirc;ng cụ PC Mark nhằm xem video, lướt web v&agrave; chỉnh sửa ảnh li&ecirc;n tục, kết quả cho thấy pin M5 mini c&oacute; thể chạy li&ecirc;n tục tới hơn 10 giờ, từ mức pin nạp đầy 100% cho tới khi c&ograve;n 10%. Trong khi th&ocirc;ng thường ở b&agrave;i test n&agrave;y,&nbsp;kết quả hơn 7 giờ&nbsp;l&agrave; đủ tốt với một smartphone Android.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ưu điểm d&agrave;i ng&agrave;y, pin của M5 mini c&ograve;n c&oacute; thể sạc cho những smartphone Android kh&aacute;c, thậm ch&iacute; cả iPhone hay Windows Phone, như một cục pin dự ph&ograve;ng. D&ugrave; vậy, để c&oacute; thể sạc được, người d&ugrave;ng cần c&oacute; th&ecirc;m một c&aacute;p microUSB OTG, phụ kiện c&oacute; gi&aacute; khoảng 50.000 đồng nhưng kh&ocirc;ng được tặng k&egrave;m theo m&aacute;y.&nbsp;Ngo&agrave;i sạc, M5 mini c&ograve;n c&oacute; thể d&ugrave;ng c&aacute;p v&agrave; đầu chuyển USB OTG để lấy ảnh từ iPhone hay smartphone kh&aacute;c trực tiếp, kh&ocirc;ng cần chuyển qua m&aacute;y t&iacute;nh, thẻ nhớ hay kết nối kh&ocirc;ng d&acirc;y như Bluetooth...</p>\r\n\r\n<p>D&ugrave; vậy, pin lớn v&agrave; l&acirc;u cũng k&eacute;o theo nhược điểm sạc pin chậm.&nbsp;Củ sạc k&egrave;m m&aacute;y đ&atilde; được n&acirc;ng d&ograve;ng điện ra l&ecirc;n 2A - 5,2V, nhưng thời gian d&ugrave;ng n&oacute; để nạp đầy ho&agrave;n to&agrave;n cho pin 4.000 mAh vẫn mất tới 3 giờ.&nbsp;</p>\r\n\r\n<p>Nếu phải so với một số model pin &quot;khủng&quot; gi&aacute; rẻ kh&aacute;c của Lenovo hay Philips, kiểu d&aacute;ng Gionee M5 mini c&oacute; n&eacute;t hiện đại hơn. V&igrave; vỏ rời nhưng mặt lưng l&agrave;m từ kim loại cho cảm gi&aacute;c cầm cứng v&agrave; chắc chắn, tr&ocirc;ng kh&ocirc;ng kh&aacute;c thiết kế nguy&ecirc;n khối. Mặt k&iacute;nh ph&iacute;a trước được miết cong kiểu thiết kế 2,5D với viền đen m&agrave;n h&igrave;nh được giấu đi, tạo cảm gi&aacute;c điện thoại c&oacute; thiết kế m&agrave;n h&igrave;nh tr&agrave;n viền.&nbsp;M&aacute;y c&oacute; hai lựa chọn m&agrave;u sắc, ghi bạc v&agrave; v&agrave;ng, đi k&egrave;m khung viền giả kim loại với v&acirc;n xước.</p>\r\n\r\n<p>Điểm đ&aacute;ng c&acirc;n nhắc ở Gionee l&agrave; cảm gi&aacute;c cầm rất nặng so với những smartphone c&ugrave;ng cỡ m&agrave;n h&igrave;nh 5 inch. M&aacute;y kh&ocirc;ng qu&aacute; d&agrave;y&nbsp;(9,6 mm)&nbsp;nhưng trọng lượng th&igrave; hơn hẳn c&aacute;c smartphone 5 inch kh&aacute;c. M5 mini nặng tới gần 190 gram, ngang với c&aacute;c phablet vỏ kim loại như iPhone 6 Plus hay 6s Plus v&agrave; thậm ch&iacute; c&ograve;n nặng hơn cả Galaxy Note 5. Dẫu vậy,&nbsp;nếu phải so s&aacute;nh với việc cầm th&ecirc;m một vi&ecirc;n pin dự ph&ograve;ng nữa, chỉ cầm mỗi M5 mini vẫn tiện hơn.&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng nổi trội như pin, nhưng Gionee M5 mini sở hữu một m&agrave;n h&igrave;nh đủ d&ugrave;ng với k&iacute;ch thước 5 inch v&agrave; độ ph&acirc;n giải HD 720p, c&oacute; chất lượng hiển thị tốt hơn Galaxy J2 của Samsung hay Neo 5 của Oppo. Tấm nền IPS đem lại h&igrave;nh ảnh hiển thị n&eacute;t, g&oacute;c nh&igrave;n ổn v&agrave; m&agrave;u sắc kh&aacute; trung thực, cảm ứng nhạy. D&atilde;y ph&iacute;m cảm ứng cũng được t&aacute;ch ri&ecirc;ng khỏi m&agrave;n h&igrave;nh n&ecirc;n phần hiển thị kh&ocirc;ng bị ảnh hưởng. Điểm kh&ocirc;ng th&iacute;ch ở M5 mini l&agrave; m&agrave;n h&igrave;nh bị b&oacute;ng kh&aacute; nhiều v&agrave; hơi kh&oacute; nh&igrave;n khi ngo&agrave;i trời, nơi nhiều đ&egrave;n s&aacute;ng.&nbsp;</p>\r\n\r\n<p>T&iacute;nh năng tr&ecirc;n M5 mini kh&ocirc;ng ấn tượng v&igrave; thực tế đ&acirc;y chỉ l&agrave; một model gi&aacute; rẻ. Gionee th&ecirc;m một số tiện &iacute;ch cảm biến th&ocirc;ng minh như lật m&aacute;y để tắt chu&ocirc;ng, chạm 2 lần để mở m&agrave;n h&igrave;nh, hay chia sẻ qua giao tiếp HotKnox (smartphone d&ugrave;ng chip MediaTek).&nbsp;Nhưng giao diện mới tạo ra kh&aacute;c biệt cho Gionee M5 mini so với những mẫu Android kh&aacute;c.&nbsp;</p>\r\n\r\n<p>Phi&ecirc;n bản 5.1 Lollipop được tuỳ biến v&agrave; mang t&ecirc;n Amigo, loại bỏ khay quản l&yacute; ứng dụng đặc trưng của Android. Thanh quản l&yacute; c&agrave;i đặt nhanh gồm Wi-Fi, Bluetooth, độ s&aacute;ng m&agrave;n h&igrave;nh xuất hiện khi vuốt tay từ cạnh dưới m&agrave;n h&igrave;nh l&ecirc;n, giống iOS tr&ecirc;n iPhone. Trong khi vuốt từ cạnh tr&ecirc;n xuống chỉ ra khu vực quản l&yacute; th&ocirc;ng b&aacute;o.&nbsp;</p>\r\n\r\n<p>So với Android th&ocirc;ng thường, thiết kế giao diện n&agrave;y cho thao t&aacute;c nhanh hơn đ&ocirc;i ch&uacute;t.&nbsp;Tuy nhi&ecirc;n, giao diện của Gionee M5 mini chạy chưa mượt, c&oacute; hiện tượng giật sau khi d&ugrave;ng m&aacute;y một thời gian hoặc khi vuốt qua lại giữa c&aacute;c m&agrave;n h&igrave;nh ứng dụng. Dung lượng RAM hoạt động của m&aacute;y thường ở mức 1,1 đến 1,2GB trong tổng số 2GB.</p>\r\n', 'public/images/20160418013708mini.jpg', 'T', 1, '2016-04-18', 'Có giá 3 triệu đồng, nhưng smartphone Android 2 sim của Gionee sở hữu pin tới 4.000 mAh, có thể dùng thông thường tới 3 ngày và sạc cho điện thoại khác khi cần.'),
(14, 'Tại sao nên ăn ớt mỗi ngày', '<p>Ớt chứa nhiều vitamin C, A v&agrave; chất capsaicin&nbsp;gi&uacute;p k&iacute;ch th&iacute;ch vị gi&aacute;c để ta ăn ngon miệng hơn v&agrave; c&oacute; t&aacute;c dụng chống oxy h&oacute;a, gi&uacute;p trẻ h&oacute;a l&agrave;n da.</p>\r\n', 'public/images/20160418015113ot.jpg', 'H', 1, '2016-04-18', 'Ớt chứa nhiều vitamin C, A và chất capsaicin giúp kích thích vị giác để ta ăn ngon miệng hơn và có tác dụng chống oxy hóa, giúp trẻ hóa làn da.'),
(15, 'Điều gì xảy ra nếu bạn ngừng ăn thịt', '<p>Dưới đ&acirc;y l&agrave; những t&aacute;c dụng của việc hạn chế thịt trong bữa ăn h&agrave;ng ng&agrave;y, theo&nbsp;<em>Boldsky</em>.</p>\r\n\r\n<p>Giảm axit</p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia cho rằng thực phẩm từ thịt c&oacute; thể k&iacute;ch th&iacute;ch việc sản xuất axit trong dạ d&agrave;y dẫn đến c&aacute;c bệnh như axit dư thừa, ợ n&oacute;ng, đau đầu, đau dạ d&agrave;y... Trong khi một chế độ ăn gi&agrave;u thực vật sẽ chống lại việc sản xuất axit trong dạ d&agrave;y của bạn.</p>\r\n\r\n<p>Giảm c&acirc;n</p>\r\n\r\n<p>C&aacute;c nh&agrave; khoa học tiến h&agrave;nh một nghi&ecirc;n cứu trong đ&oacute; những người ăn thịt ho&agrave;n to&agrave;n chuyển sang chế độ ăn thực vật th&igrave; trọng lượng của họ giảm xuống đ&aacute;ng kể m&agrave; kh&ocirc;ng cần nhiều nỗ lực. V&igrave; vậy, nếu bạn đang thừa c&acirc;n, h&atilde;y lựa chọn chế độ ăn gi&agrave;u thực vật.</p>\r\n\r\n<p>Hệ thống ruột khỏe mạnh</p>\r\n\r\n<p>Những người ăn theo chế độ ăn thực vật, hệ thống ti&ecirc;u h&oacute;a sạch sẽ hơn. Chế độ ăn chay gi&uacute;p th&uacute;c đẩy sự tăng trưởng của vi khuẩn l&agrave;nh mạnh, ngăn ngừa rối loạn ti&ecirc;u h&oacute;a. Trong khi chế độ ăn nhiều thịt c&oacute; thể g&acirc;y tổn hại cho ruột do c&aacute;c chất bảo quản v&agrave; k&iacute;ch th&iacute;ch tố được sử dụng trong c&aacute;c sản phẩm động vật.</p>\r\n\r\n<p>Tốt cho da</p>\r\n\r\n<p>Một số nghi&ecirc;n cứu đ&atilde; cho thấy rằng c&aacute;c hormone ti&ecirc;m v&agrave;o động vật trước khi ti&ecirc;u thụ c&oacute; thể ảnh hưởng đến l&agrave;n da của con người bằng c&aacute;ch g&acirc;y ra c&aacute;c vấn đề như mụn trứng c&aacute;, trứng c&aacute; đỏ, ch&agrave;m... Chuyển đổi sang một chế độ ăn nhiều rau xanh c&oacute; thể gi&uacute;p đem lại cho bạn một l&agrave;n da khỏe mạnh bởi rau quả rất gi&agrave;u chất chống oxy h&oacute;a v&agrave; chất xơ.</p>\r\n\r\n<p>Giảm nguy cơ tiểu đường</p>\r\n\r\n<p>C&aacute;c nghi&ecirc;n cứu đ&atilde; t&igrave;m thấy rằng, những người ăn thịt c&oacute; nguy cơ mắc bệnh tiểu đường loại 2 nhiều hơn so với những người ăn chay. L&yacute; do l&agrave; sự li&ecirc;n kết giữa hormone trong thịt &nbsp;với sắt v&agrave; nitrat, đặc biệt l&agrave; trong thịt đỏ g&acirc;y ra bệnh.</p>\r\n\r\n<p>Giảm cholesterol m&aacute;u</p>\r\n\r\n<p>Thực phẩm thịt chứa h&agrave;m lượng cao chất b&eacute;o b&atilde;o h&ograve;a l&agrave;m tăng mức độ cholesterol trong m&aacute;u. Điều n&agrave;y c&oacute; thể dẫn đến c&aacute;c bệnh nghi&ecirc;m trọng như bệnh b&eacute;o ph&igrave;, đột quỵ, bệnh tim mạch, huyết &aacute;p...</p>\r\n\r\n<p>Giảm nguy cơ nhiễm tr&ugrave;ng</p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia n&oacute;i rằng, hạn chế ăn thịt sẽ giảm nhiễm tr&ugrave;ng v&agrave; vi&ecirc;m xảy ra trong cơ thể. Nếu con vật bị nhiễm tr&ugrave;ng, n&oacute; c&oacute; thể l&acirc;y lan cho con người nếu ch&uacute;ng ta ti&ecirc;u thụ thịt của n&oacute;.&nbsp;</p>\r\n\r\n<p>C&aacute;c gen khỏe mạnh</p>\r\n\r\n<p>Một nghi&ecirc;n cứu tuy&ecirc;n bố, chỉ cần dựa v&agrave;o chế độ ăn uống sẽ biết được ADN khỏe mạnh hay yếu k&eacute;m. C&aacute;c chất chống oxy h&oacute;a v&agrave; dinh dưỡng được t&igrave;m thấy trong rau quả cũng c&oacute; thể gi&uacute;p ADN sửa chữa v&agrave; l&agrave;m giảm sản xuất c&aacute;c tế b&agrave;o ung thư. Ngo&agrave;i ra, một chế độ ăn uống gi&agrave;u thực vật gi&uacute;p giảm ngăn chặn qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a, giữ cơ thể trẻ trung hơn.</p>\r\n', 'public/images/20160418015833thit.jpg', 'H', 3, '2016-04-18', 'Nếu bạn ngừng ăn thịt, cơ thể sẽ có những biến đổi tích cực như giảm nguy cơ bệnh tật, ngăn chặn lão hóa và giữ cơ thể khỏe mạnh, trẻ trung.'),
(16, 'Thực hư dầu thực vật tốt cho sức khỏe', '<p>Theo<em>&nbsp;Fox News</em>, nhiều nghi&ecirc;n cứu trước đ&acirc;y từng chỉ ra chất b&eacute;o kh&ocirc;ng b&atilde;o h&ograve;a đa trong dầu thực vật tốt cho sức khỏe. Dựa v&agrave;o đ&oacute;, kh&ocirc;ng &iacute;t người đ&atilde; thay đổi th&oacute;i quen, sử dụng dầu từ đậu n&agrave;nh, ng&ocirc;, &ocirc; liu thay cho dầu mỡ động vật. Thế nhưng, h&oacute;a ra c&aacute;c c&ocirc;ng tr&igrave;nh ấy chưa bao giờ được tiến h&agrave;nh theo đ&uacute;ng chuẩn m&agrave; chỉ xem x&eacute;t ngẫu nghi&ecirc;n tr&ecirc;n một số t&igrave;nh nguyện vi&ecirc;n để t&igrave;m hiểu thực phẩm c&ugrave;ng chất b&eacute;o t&aacute;c động đến cơ thể như thế n&agrave;o.</p>\r\n\r\n<p>Mới đ&acirc;y, tiến sĩ&nbsp;Christopher Ramsden từ Viện Sức khỏe Quốc gia Mỹ đ&atilde; kiểm chứng lợi &iacute;ch của dầu thực vật v&agrave; ph&aacute;t hiện lượng cholesterol thấp nhờ hấp thụ dầu thực vật kh&ocirc;ng hề l&agrave;m tăng tuổi thọ.</p>\r\n\r\n<p>&quot;Tr&ecirc;n thực tế, những người giảm qu&aacute; nhiều cholesterol dễ chết sớm&quot;, tiến sĩ&nbsp;Ramsden cho biết. &Ocirc;ng c&ugrave;ng cộng sự chia&nbsp;9.400 người tuổi 20-97 th&agrave;nh&nbsp;hai nh&oacute;m: một nh&oacute;m ăn dầu ng&ocirc; v&agrave; bơ l&agrave;m từ ng&ocirc;, một nh&oacute;m vẫn ăn mỡ động vật. Sau khi chuyển sang chế độ ăn dầu thực vật, nồng độ cholesterol của nh&oacute;m thứ nhất giảm gần 14% nhưng sức khỏe kh&ocirc;ng c&oacute; dấu hiệu chuyển biển t&iacute;ch cực n&agrave;o đ&aacute;ng kể.</p>\r\n\r\n<p>Tr&ecirc;n thực tế, Hiệp hội Tim mạch Mỹ khuyến c&aacute;o tổng cholesterol trong huyết thanh&nbsp;(bao gồm triglyceride, cholesterol LDL v&agrave; cholesterol HDL) dưới 180 mg/dL l&agrave; l&yacute; tưởng. Cứ mỗi 30 mg/dL bị hụt đi, nguy cơ tử vong của con người lại tăng 22%. Ngo&agrave;i ra, dầu thực vật kh&ocirc;ng t&aacute;c dụng g&igrave; đối với bệnh xơ vữa động mạch.</p>\r\n\r\n<p>Tiến sĩ Lennert Veerman, nh&agrave; nghi&ecirc;n cứu y tế c&ocirc;ng cộng từ Đại học Queensland (Australia) nhận định vẫn cần th&ecirc;m c&aacute;c nghi&ecirc;n cứu kh&aacute;c để x&aacute;c định loại dầu mỡ n&agrave;o l&agrave; tốt nhất. &quot;Ch&uacute;ng ta n&ecirc;n tập trung hạn chế chất b&eacute;o chuyển h&oacute;a, đường, muối v&agrave; ăn theo chế độ dinh dưỡng phong ph&uacute; nhiều rau quả, ngũ cốc nguy&ecirc;n hạt, hải sản, thịt nạc, trứng, đậu c&ugrave;ng c&aacute;c loại hạt&quot;, Veerman n&oacute;i. Cuối c&ugrave;ng, d&ugrave; cho dầu thực vật đem lại lợi &iacute;ch cho mạch m&aacute;u hay kh&ocirc;ng, bạn cũng kh&ocirc;ng cần ngừng sử dụng bởi chưa bằng chứng x&aacute;c thực n&agrave;o cho thấy dầu thực vật g&acirc;y hại.</p>\r\n', 'public/images/20160418020015dau.jpg', 'H', 3, '2016-04-18', 'Ăn dầu thực vật, cơ thể giảm lượng chất béo hòa tan và cholesterol nhưng không có tác dụng hạn chế nguy cơ bệnh tim mạch hoặc tăng tuổi thọ.');
INSERT INTO `news` (`idnews`, `title`, `content`, `image`, `categorynews`, `id`, `datepost`, `summary`) VALUES
(17, 'Trái cây màu đỏ giúp giảm cân nhanh chóng', '<p>Tr&aacute;i c&acirc;y m&agrave;u đỏ &iacute;t calo, giảm cảm gi&aacute;c th&egrave;m ăn v&agrave; cung cấp cho cơ thể lượng lớn protein v&agrave; chất xơ. Tr&aacute;i c&acirc;y m&agrave;u đỏ như quả lựu, t&aacute;o, mận, anh đ&agrave;o... rất gi&agrave;u vitamin C c&oacute; khả năng miễn dịch v&agrave; tăng cường sự trao đổi chất của cơ thể.&nbsp;</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; những loại quả c&oacute; m&agrave;u đỏ gi&uacute;p giảm c&acirc;n, theo&nbsp;<em>Boldsky.</em></p>\r\n\r\n<p>Quả lựu</p>\r\n\r\n<p>Ăn lựu thường xuy&ecirc;n, bạn kh&ocirc;ng chỉ giảm c&acirc;n m&agrave; c&ograve;n tr&aacute;nh nhiều loại bệnh tật v&igrave; c&aacute;c chất oxy h&oacute;a trong lựu tăng cường hệ miễn dịch.&nbsp;</p>\r\n\r\n<p>T&aacute;o</p>\r\n\r\n<p>T&aacute;o đỏ hay t&aacute;o xanh đều rất gi&agrave;u vitamin C, nhưng t&aacute;o đỏ c&oacute; nhiều lợi &iacute;ch sức khỏe hơn. T&aacute;o đỏ chứa nhiều chất xơ gi&uacute;p no l&acirc;u, giảm cơn th&egrave;m ăn, qua đ&oacute; gi&uacute;p giảm c&acirc;n hiệu quả.&nbsp;</p>\r\n\r\n<p>Mận</p>\r\n\r\n<p>Mận c&oacute; chất dinh dưỡng v&agrave; vitamin hỗ trợ hệ miễn dịch. Ăn mận v&agrave;o buổi s&aacute;ng gi&uacute;p đốt ch&aacute;y lượng chất b&eacute;o dư thừa. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<p>Anh đ&agrave;o đỏ</p>\r\n\r\n<p>Anh đ&agrave;o m&agrave;u đỏ n&ecirc;n ăn khi dạ d&agrave;y trống rỗng. Loại quả n&agrave;y tốt cho người bị bệnh g&uacute;t, l&agrave;m giảm mức axit trong m&aacute;u v&agrave; do đ&oacute; ngăn ngừa c&aacute;c vấn đề g&uacute;t.</p>\r\n\r\n<p>D&acirc;u</p>\r\n\r\n<p>D&acirc;u chứa vitamin cần thiết cho cơ thể, n&ecirc;n ăn hai lần trong tuần.</p>\r\n', 'public/images/20160418020130do.jpg', 'H', 3, '2016-04-18', 'Muốn giảm cân, ăn quả màu đỏ như lựu, táo, anh đào... giàu vitamin C, có khả năng miễn dịch và tăng cường sự trao đổi chất của cơ thể.'),
(18, 'Vì sao bữa ăn không thể thiếu rau quả', '<p>Rau v&agrave; hoa quả l&agrave; một trong 4 nh&oacute;m thực phẩm cần thiết để c&oacute; một bữa ăn hợp l&yacute; ở gia đ&igrave;nh. Gi&aacute; trị của rau v&agrave; hoa quả l&agrave; cung cấp cho cơ thể c&aacute;c chất dinh dưỡng c&oacute; hoạt t&iacute;nh sinh học cao, đặc biệt l&agrave; vitamin C, chất kho&aacute;ng v&agrave; vi kho&aacute;ng. Ngo&agrave;i ra rau quả chứa c&aacute;c acid hữu cơ, xenluloza, chất chống oxy h&oacute;a n&ecirc;n c&oacute; t&aacute;c dụng n&acirc;ng cao sức khỏe v&agrave; ph&ograve;ng chống c&aacute;c bệnh mạn t&iacute;nh kh&ocirc;ng l&acirc;y.</p>\r\n\r\n<p>Hơn 80% nguồn vitamin A sẵn c&oacute; v&agrave; gần 100% nguồn vitamin C được cung cấp từ rau quả.&nbsp;Nghi&ecirc;n cứu cho thấy, c&aacute;c loại rau như: rau ng&oacute;t, rau đay, rau dền l&agrave; những loại c&oacute; gi&aacute; trị dinh dưỡng cao, đồng đều về h&agrave;m lượng caroten, vitamin C, sắt, gi&agrave;u muối kho&aacute;ng, vi kho&aacute;ng, lượng protein cao gấp 3-5 lần c&aacute;c loại rau kh&aacute;c. Rau muống c&oacute; gi&aacute; trị dinh dưỡng trung b&igrave;nh, k&eacute;m rau ng&oacute;t, rau đay song được nhiều người ưa th&iacute;ch, sử dụng thường xuy&ecirc;n, quanh năm với số lượng lớn.</p>\r\n\r\n<p>C&aacute;c loại rau gia vị như m&ugrave;i, t&iacute;a t&ocirc;, h&uacute;ng, th&igrave;a l&agrave;... c&oacute; h&agrave;m lượng caroten cao v&agrave; đồng đều hơn so với rau ăn v&agrave; quả ngọt, cao nhất l&agrave; t&iacute;a t&ocirc;, h&uacute;ng quế, ớt v&agrave;ng, đồng thời gi&agrave;u sắt. Rau gia vị được sử dụng tươi sống kh&ocirc;ng bị tổn thất dinh dưỡng qua nấu nướng, n&ecirc;n gi&aacute; trị sử dụng vitamin rất cao. Ngo&agrave;i ra c&aacute;c rau gia vị c&ograve;n cung cấp nguồn kh&aacute;ng sinh thực vật rất c&oacute; gi&aacute; trị như h&agrave;nh ta, h&agrave;nh t&acirc;y, tỏi, củ cải, c&agrave; rốt, c&agrave; chua, b&iacute; ng&ocirc;, ớt&hellip;</p>\r\n\r\n<p>C&aacute;c loại quả ch&iacute;n m&agrave;u v&agrave;ng, đỏ, da cam như đu đủ, hồng, dưa hấu, cam, qu&yacute;t v&agrave;ng c&oacute; h&agrave;m lượng caroten kh&aacute; cao v&agrave; gi&agrave;u sắt. Đặc biệt m&agrave;ng gấc tươi c&oacute; h&agrave;m lượng caroten đặc biệt cao. Đ&acirc;y l&agrave; một loại quả qu&yacute; c&oacute; khả năng ph&ograve;ng bệnh thiếu vitamin A, chống oxy h&oacute;a v&agrave; ung thư.</p>\r\n\r\n<p>Rau l&agrave; nguồn cung cấp vitamin, kho&aacute;ng chất v&agrave; chất xơ gi&uacute;p đ&agrave;o thải chất độc cholesterol ra khỏi cơ thể v&agrave; chống t&aacute;o b&oacute;n. Một số rau gia vị c&ograve;n c&oacute; t&aacute;c dụng chữa bệnh nhờ c&oacute; c&aacute;c tinh dầu v&agrave; kh&aacute;ng sinh thực vật. H&agrave;m lượng c&aacute;c sinh tố v&agrave; chất xơ trong rau lại cao hơn so với tr&aacute;i c&acirc;y.</p>\r\n\r\n<p>Hiện nay, trong bữa ăn gia đ&igrave;nh, hoa quả đ&atilde; trở th&agrave;nh một thứ kh&ocirc;ng thể thiếu. Một số người lo lắng rau xanh kh&ocirc;ng đảm bảo về an to&agrave;n thực phẩm v&agrave; c&oacute; thể bớt lượng rau ăn v&agrave;o &iacute;t đi một ch&uacute;t hoặc kh&ocirc;ng ăn rau cũng được nhưng tr&aacute;i c&acirc;y nhất định phải c&oacute; v&agrave; coi n&oacute; như một thức ăn thay thế rau xanh. Tuy nhi&ecirc;n d&ugrave;ng tr&aacute;i c&acirc;y để thay thế ho&agrave;n to&agrave;n rau lại kh&ocirc;ng ph&ugrave; hợp với khoa học dinh dưỡng.</p>\r\n\r\n<p>C&aacute;c chất xơ trong rau c&oacute; cấu tr&uacute;c mịn m&agrave;ng, h&agrave;m lượng cao hơn trong hoa quả gi&uacute;p cho cơ thể hấp thụ c&aacute;c chất dinh dưỡng c&oacute; trong 3 nh&oacute;m thức ăn cơ bản (nh&oacute;m đạm, đường, b&eacute;o). Nếu chỉ ăn đơn thuần c&aacute;c protein động vật, hiệu suất hấp thụ protein tr&ecirc;n đường ti&ecirc;u h&oacute;a chỉ l&agrave; 70%, c&ograve;n nếu ăn th&ecirc;m rau th&igrave; hiệu suất hấp thụ protein sẽ đạt 90%.</p>\r\n\r\n<p>Rau c&oacute; &yacute; nghĩa v&ocirc; c&ugrave;ng quan trọng đối với sức khỏe v&agrave; tuổi thọ v&igrave; h&agrave;m lượng chất xơ nhiều hơn trong hoa quả. V&igrave; vậy kh&ocirc;ng thể d&ugrave;ng tr&aacute;i c&acirc;y để thay thế rau xanh trong mỗi bữa ăn h&agrave;ng ng&agrave;y của gia đ&igrave;nh. N&ecirc;n sử dụng đủ rau v&agrave; quả ch&iacute;n quanh năm với lượng trung b&igrave;nh l&agrave; 400 g tr&ecirc;n người mỗi ng&agrave;y.</p>\r\n', 'public/images/20160418020412rau.jpg', 'H', 3, '2016-04-18', 'Ăn ít rau và hoa quả được xem là nguyên nhân của 1,7 triệu trường hợp tử vong trên thế giới và 19% ca ung thư dạ dày ruột.'),
(19, 'Họa ung thư từ thực phẩm bẩn', '<p>Năm 2000, Việt Nam chỉ c&oacute; khoảng 69.000 ca mắc ung thư th&igrave; tới năm 2010, con số n&agrave;y đ&atilde; tăng gần gấp đ&ocirc;i l&ecirc;n hơn 126.000 ca. Nhiều người đặt c&acirc;u hỏi v&igrave; sao Việt Nam c&agrave;ng ng&agrave;y c&agrave;ng c&oacute; nhiều bệnh nh&acirc;n ung thư đến vậy.&nbsp;</p>\r\n\r\n<p>Tiến sĩ Từ Ngữ, Tổng thư k&yacute; Hội Dinh dưỡng Việt Nam nhấn mạnh, kh&ocirc;ng chỉ ung thư nhiều m&agrave; tất cả c&aacute;c bệnh kh&aacute;c đều nhiều. Trong x&atilde; hội văn minh, bệnh tật ph&aacute;t triển theo hướng bệnh kh&ocirc;ng l&acirc;y nhiễm trong đ&oacute; c&oacute; ung thư.&nbsp;</p>\r\n\r\n<p>Theo &ocirc;ng nguy&ecirc;n nh&acirc;n khiến số ca mắc ung thư nhiều hơn c&oacute; thể đề cập đến 2 yếu tố quan trọng. Thứ nhất l&agrave; tập qu&aacute;n ăn thay đổi. Ng&agrave;y xưa, chế độ ăn của người Việt l&agrave; cơm - canh - c&aacute;; canh l&agrave; rau, nước; ăn l&agrave; thực phẩm ho&agrave;n to&agrave;n tự nhi&ecirc;n kh&ocirc;ng dự trữ, dự trữ chỉ c&oacute; muối vừng lạc, tương c&agrave;. Trong khi hiện nay, thực phẩm c&ocirc;ng nghiệp rất nhiều: m&igrave; t&ocirc;m, x&uacute;c x&iacute;ch, giăm b&ocirc;ng thịt hun kh&oacute;i... yếu tố g&acirc;y ung thư l&agrave; thịt đỏ.&nbsp;</p>\r\n\r\n<p>Thứ hai l&agrave; do sự ph&aacute;t triển của ng&agrave;nh n&ocirc;ng nghiệp, c&aacute;ch trồng trọt chăn nu&ocirc;i kh&aacute;c. Trước canh t&aacute;c ph&acirc;n chuồng, ph&acirc;n xanh - b&egrave;o hoa d&acirc;u, những thứ n&agrave;y v&ocirc; hại; giờ thay v&agrave;o đ&oacute; l&agrave; h&oacute;a chất - c&oacute; từ c&ocirc;ng đoạn sản xuất cho đến trước khi ti&ecirc;u d&ugrave;ng. Thực phẩm c&ocirc;ng nghiệp phải c&oacute; chất bảo quản, m&agrave;u sắc, chất điều vị..., cả quy tr&igrave;nh ấy l&agrave; h&agrave;ng c&ocirc;ng nghiệp, h&oacute;a chất sử dụng nhiều hơn.</p>\r\n\r\n<p>&quot;Dinh dưỡng kh&ocirc;ng tạo n&ecirc;n ung thư, quan trọng l&agrave; c&aacute;ch ăn sai. Nguy&ecirc;n nh&acirc;n ung thư do ăn uống, m&ocirc;i trường, di truyền, dinh dưỡng chỉ l&agrave; yếu tố thuận lợi. Tuy nhi&ecirc;n c&oacute; nghi&ecirc;n cứu người ch&acirc;u &Aacute;, đặc biệt l&agrave; c&aacute;c nước hay ăn m&oacute;n dưa l&ecirc;n men - dưa muối, kim chi th&igrave; ung thư đường ti&ecirc;u h&oacute;a cao hơn so với c&aacute;c nước kh&ocirc;ng ăn loại thức ăn n&agrave;y. Nhiều t&agrave;i liệu n&oacute;i kim loại nặng như thủy ng&acirc;n, thuốc trừ s&acirc;u diệt cỏ cũng g&acirc;y ung thư&quot;, tiến sĩ Từ Ngữ nhấn mạnh.</p>\r\n\r\n<p>Tiến sĩ Nguyễn B&aacute; Đức, Ph&oacute; Chủ tịch Hội Ung thư Việt Nam cũng cho rằng số ca mắc ung thư tăng nhanh do 3 nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave;: thực phẩm bẩn, m&ocirc;i trường &ocirc; nhiễm, do tuổi thọ tăng. Bản th&acirc;n thực phẩm kh&ocirc;ng c&oacute; hại nhưng c&aacute;c h&oacute;a chất trong thực phẩm l&agrave; nguy hại nhất. Qua ăn uống, rất nhiều chất độc hại v&agrave;o cơ thể m&agrave; mọi người kh&ocirc;ng hay.&nbsp;Trước kia,&nbsp;rau củ quả kh&ocirc;ng c&oacute; thuốc trừ s&acirc;u, c&oacute; chất k&iacute;ch th&iacute;ch tăng trưởng, nay th&igrave; ngược lại. Ngo&agrave;i ra để thức ăn được đẹp, để được l&acirc;u, người b&aacute;n kh&ocirc;ng ngần ngại đổ chất bảo quản v&agrave;o thịt c&aacute;, hoa quả, đồ ăn ch&iacute;n, đồ đ&oacute;ng hộp, d&ugrave;ng h&oacute;a chất c&ocirc;ng nghiệp nhuộm m&agrave;u thực phẩm...</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, c&aacute;c loại đồ chi&ecirc;n r&aacute;n ở nhiệt độ cao sẽ sinh ra c&aacute;c amin dị v&ograve;ng c&oacute; khả năng g&acirc;y ung thư. Đồ nướng tr&ecirc;n than như thịt xi&ecirc;n nướng, b&uacute;n chả sẽ h&igrave;nh th&agrave;nh c&aacute;c hydrocarbon thơm đa v&ograve;ng, đơn cử như pyrene (c&oacute; trong nhựa đường v&agrave; bồ h&oacute;ng) g&acirc;y ung thư....</p>\r\n\r\n<p>Trong thức ăn c&oacute; qu&aacute; nhiều chất b&eacute;o, đặc biệt l&agrave; chất b&eacute;o động vật, cộng chế độ ăn &iacute;t hoa quả, rau xanh kết hợp &iacute;t vận động c&oacute; thể g&acirc;y ra một số loại ung thư như đại tr&agrave;ng, ung thư v&uacute; ở nữ. Đặc biệt nếu sử dụng c&aacute;c thực phẩm c&oacute; thuốc bảo quản vượt ngưỡng, bản th&acirc;n thực phẩm kh&ocirc;ng g&acirc;y ung thư nhưng chất bảo quản g&acirc;y ung thư. Một số chất sản sinh ra trong qu&aacute; tr&igrave;nh bảo quản thực phẩm như aflatoxin trong gạo mốc g&acirc;y ung thư gan.&nbsp;C&aacute;c chất độc hại trong thực phẩm l&agrave; nguy&ecirc;n nh&acirc;n của tr&ecirc;n 30% bệnh nh&acirc;n ung thư.</p>\r\n', 'public/images/20160418053411tp.jpg', 'H', 1, '2016-04-18', 'Sau 10 năm số ca mắc ung thư được báo cáo tại Việt Nam tăng gần gấp đôi, dự kiến đến năm 2020 con số này sẽ tăng vọt lên 200.000 ca.'),
(20, 'Đồng hồ Gear S2 đã có thể kết nối với iPhone', '<p>Đầu năm 2016, nh&agrave; sản xuất H&agrave;n Quốc hứa hẹn mở rộng kết nối smartwatch Gear S2 với iPhone trong năm nay, thay v&igrave; chỉ d&ugrave;ng được với c&aacute;c mẫu Android. Trong lộ tr&igrave;nh thực hiện, phần mềm Gear Manager cho iOS đ&atilde; được chia sẻ ra ngo&agrave;i, trước khi Samsung tung bản ch&iacute;nh thức l&ecirc;n App Store.</p>\r\n\r\n<p>Người d&ugrave;ng cần tải ứng dụng&nbsp;rồi c&agrave;i đặt l&ecirc;n iPhone th&ocirc;ng qua iTunes tr&ecirc;n m&aacute;y t&iacute;nh. Do Gear Manager tr&ecirc;n iOS chưa phải bản ch&iacute;nh thức n&ecirc;n cần th&ecirc;m bước x&aacute;c nhận trong mục Device Management. Sau khi ho&agrave;n th&agrave;nh, việc kết nối đồng hồ th&ocirc;ng minh cao cấp nhất của Samsung với iPhone thực hiện tương tự như tr&ecirc;n c&aacute;c m&aacute;y Android.</p>\r\n\r\n<p>Thử nghiệm cho thấy Gear S2 hoạt động tốt với điện thoại của Apple. C&aacute;c t&iacute;nh năng ch&iacute;nh đều đ&atilde; sử dụng được, th&ocirc;ng b&aacute;o đến iPhone được đẩy tức th&igrave; sang đồng hồ th&ocirc;ng minh. Theo&nbsp;<em>TheVerge</em>, Samsung sẽ ch&iacute;nh thức hỗ trợ Gear S2 với iPhone v&agrave;o cuối th&aacute;ng n&agrave;y.</p>\r\n\r\n<p>Trong c&aacute;c mẫu đồng hồ th&ocirc;ng minh của Samsung, Gear S2 được đ&aacute;nh gi&aacute; cao nhất về kiểu d&aacute;ng. Trong khi mẫu Gear S2 thường c&oacute; thiết kế hiện đại, trẻ trung với d&acirc;y cao su, th&igrave; Gear S2 Classic lại c&oacute; kiểu d&aacute;ng cổ điển, giống với đồng hồ truyền thống hơn, d&acirc;y bằng da thật.</p>\r\n\r\n<p>Sản phẩm c&oacute; mặt hiển thị dạng tr&ograve;n, m&agrave;n h&igrave;nh cảm ứng k&iacute;ch thước 1,2 inch. Gear S2 chạy&nbsp;hạy hệ điều h&agrave;nh Tizen OS với điểm độc đ&aacute;o l&agrave; v&ograve;ng xoay điều hướng quanh mặt c&ugrave;ng hai n&uacute;t Home v&agrave; Back nằm ở cạnh b&ecirc;n cho ph&eacute;p truy cập v&agrave;o c&aacute;c th&ocirc;ng b&aacute;o v&agrave; ứng dụng nhanh ch&oacute;ng, dễ d&agrave;ng.</p>\r\n\r\n<p>Samsung Galaxy Gear S2 được b&aacute;n ra với gi&aacute; 6,5 triệu đồng c&ograve;n model Gear S2 Classic đắt hơn 1 triệu đồng.&nbsp;</p>\r\n', 'public/images/20160418032816g2.jpg', 'T', 3, '2016-04-18', 'Ứng dụng Gear Manager cho iOS xuất hiện bản thử, cho phép đồng hồ thông minh của Samsung giao tiếp với smartphone của Apple.'),
(76, 'NASA thử nghiệm hệ thống động cơ proton mới cho tàu vũ trụ', '<p>N&oacute; c&oacute; thể bay tới v&ugrave;ng đệm nhật quyển (heliopause) của hệ Mặt Trời trong v&ograve;ng 10 năm. T&agrave;u Voyager 1 phải mất tới 35 năm để bay tới v&ugrave;ng n&agrave;y, theo&nbsp;<em>NASA</em>.</p>\r\n\r\n<p>Hệ thống n&agrave;y c&oacute; t&ecirc;n gọi l&agrave; Herts E-sail, c&oacute; thể hoạt động m&agrave; kh&ocirc;ng cần chất nổ đẩy. Thay v&agrave;o đ&oacute;, n&oacute; sẽ sử dụng năng lượng từ gi&oacute; Mặt Trời để đi tới r&igrave;a Th&aacute;i Dương hệ.</p>\r\n\r\n<p>Một con t&agrave;u vũ trụ sẽ sử dụng 10 đến 20 d&acirc;y nh&ocirc;m t&iacute;ch điện để tạo ra một &quot;c&aacute;nh buồm điện tử&quot; (E-sail) khổng lồ. Mỗi d&acirc;y nh&ocirc;m c&oacute; bề d&agrave;y chỉ một mm nhưng d&agrave;i hơn 8 km, tương đương 219 s&acirc;n b&oacute;ng đ&aacute;.</p>\r\n\r\n<p>C&aacute;c c&aacute;nh buồm điện tử n&agrave;y sẽ đẩy c&aacute;c proton c&oacute; trong gi&oacute; Mặt Trời, tạo ra lực đẩy cho t&agrave;u vũ trụ.</p>\r\n\r\n<p>&quot;Mặt Trời giải ph&oacute;ng c&aacute;c proton v&agrave; electron với tốc độ rất cao, 400 đến 750 km/s. E-sail sẽ sử dụng những proton n&agrave;y để đẩy t&agrave;u vũ trụ đi&quot;, Bruce Wiegmann, kỹ sư thuộc Văn ph&ograve;ng kh&aacute;i niệm ti&ecirc;n tiến Marshall v&agrave; nh&agrave; nghi&ecirc;n cứu cơ bản của Herts E-sail cho biết.</p>\r\n\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu của Trung t&acirc;m bay vũ trụ Marshall tại Huntsville, Alabama đ&atilde; bắt đầu tiến h&agrave;nh c&aacute;c thử nghiệm dự kiến diễn ra trong hai năm.</p>\r\n\r\n<p>Những thử nghiệm n&agrave;y nhằm mục đ&iacute;ch x&aacute;c định tổng số proton bị l&agrave;m chệch hướng bởi c&aacute;c d&acirc;y nh&ocirc;m v&agrave; số lượng electron bị c&aacute;c proton n&agrave;y h&uacute;t.</p>\r\n\r\n<p>C&aacute;c kỹ sư cũng sẽ kiểm tra plasma, cải thiện c&aacute;c m&ocirc; h&igrave;nh dữ liệu cho c&aacute;c bước ph&aacute;t triển E-sail trong tương lai.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo c&aacute;c nh&agrave; nghi&ecirc;n cứu, c&ograve;n rất nhiều việc phải l&agrave;m v&agrave; dự kiến phải mất 10 năm để c&oacute; thể đưa c&ocirc;ng nghệ n&agrave;y ra sử dụng trong thực tế.</p>\r\n\r\n<p>Theo l&yacute; thuyết, khi bay ngo&agrave;i vũ trụ, khu vực hiệu quả của c&aacute;c c&aacute;nh buồm điện tử cũng sẽ được n&acirc;ng cấp dần. Tại khoảng c&aacute;ch 1 đơn vị thi&ecirc;n văn AU, khu vực n&agrave;y khoảng 600 km2, nhưng ở khoảng c&aacute;ch 5 AU, n&oacute; tăng l&ecirc;n gần 1.200 km2.</p>\r\n\r\n<p>Th&ocirc;ng thường, năng lượng của c&aacute;c photon năng lượng Mặt Trời mất đi khi một c&aacute;nh buồm Mặt Trời đi tới khoảng c&aacute;ch 5 AU tại v&agrave;nh đai tiểu h&agrave;nh tinh, l&agrave;m cho qu&aacute; tr&igrave;nh gia tốc bị dừng lại. Nhưng E-sail vẫn tiếp tục tăng tốc khi vượt qua khoảng c&aacute;ch n&agrave;y, nhờ v&agrave;o c&aacute;c proton trong gi&oacute; Mặt Trời.</p>\r\n\r\n<p>&quot;Những lo ngại về việc ngừng tăng tốc kh&ocirc;ng &aacute;p dụng cho c&aacute;c proton từ gi&oacute; Mặt Trời. Với c&aacute;c d&ograve;ng chảy li&ecirc;n tục của proton v&agrave; sự gia tăng của khu vực hiệu quả, E-sail sẽ tiếp tục tăng tốc tới khoảng c&aacute;ch 16 đến 20 AU, gấp &iacute;t nhất ba lần so với buồm Mặt Trời, tốc độ cũng sẽ cao hơn rất nhiều&quot;, Wiegmann n&oacute;i.</p>\r\n\r\n<p>C&aacute;c nh&agrave; nghi&ecirc;n cứu hy vọng t&agrave;u vũ trụ trang bị c&ocirc;ng nghệ n&agrave;y c&oacute; thể bay ra ngo&agrave;i hệ Mặt Trời trong v&ograve;ng dưới 10 năm, trong khi t&agrave;u Voyager 1 của NASA tiến v&agrave;o khoảng kh&ocirc;ng ngo&agrave;i hệ Mặt Trời v&agrave;o năm 2012, 35 năm sau khi khởi h&agrave;nh.</p>\r\n\r\n<p>Ngo&agrave;i ra, E-sail c&ograve;n c&oacute; thể t&ugrave;y chỉnh cho c&aacute;c chuyến bay kh&aacute;c nhau, b&ecirc;n trong hay b&ecirc;n ngo&agrave;i c&aacute;c h&agrave;nh tinh, hoặc tới v&ugrave;ng đệm nhật quyển. Mỗi khoảng c&aacute;ch sẽ c&oacute; y&ecirc;u cầu kh&aacute;c nhau về chiều d&agrave;i d&acirc;y, số lượng d&acirc;y v&agrave; điện &aacute;p đặt v&agrave;o.</p>\r\n', 'public/images/20160418033230nasa.jpg', 'S', 3, '2016-04-18', 'Theo Cơ quan hàng không vũ trụ Mỹ (NASA), đây là hệ thống động cơ hứa hẹn có thể đưa tàu không gian ra ngoài hệ Mặt Trời với tốc độ cao hơn hiện nay rất nhiều.'),
(77, 'Trải nghiệm cận tử qua lời kể của người trong cuộc3', '<p>1Tony Cicoria, b&aacute;c sĩ phẫu thuật chỉnh h&igrave;nh:</p>\r\n\r\n<p>Tony Cicoria, b&aacute;c sĩ phẫu thuật chỉnh h&igrave;nh bị s&eacute;t đ&aacute;nh tr&uacute;ng khi đang cố gọi điện cho mẹ từ hồ Sleepy Hollow, ngoại &ocirc; New York, Mỹ, trong chuyến d&atilde; ngoại c&ugrave;ng gia đ&igrave;nh. Tia s&eacute;t truyền xuy&ecirc;n điện thoại tới đầu khiến tim &ocirc;ng ngừng đập.</p>\r\n\r\n<p>Cicoria kể lại &ocirc;ng nh&igrave;n thấy m&igrave;nh tho&aacute;t khỏi cơ thể, đi xuy&ecirc;n qua c&aacute;c bức tường tới nơi c&oacute; &aacute;nh s&aacute;ng trắng xanh trong t&acirc;m trạng h&aacute;o hức về với Ch&uacute;a. Sau khi được cứu sống, Cicoria bỗng c&oacute; đam m&ecirc; m&atilde;nh liệt với c&aacute;c bản nhạc piano cổ điển. Những giai điệu kh&ocirc;ng ngừng tự động nảy ra trong đầu, Circoria tin rằng m&igrave;nh được ph&eacute;p trở về để l&agrave;m sứ giả mang &quot;&acirc;m nhạc của thi&ecirc;n đường&quot; tới cuộc đời.</p>\r\n\r\n<p>2:sinh vi&ecirc;n cao đẳng ở Austin, Texas, Mỹ:</p>\r\n\r\n<p>C&uacute; đấu đầu &ocirc; t&ocirc; khiến Tricia Barker, một sinh vi&ecirc;n cao đẳng ở Austin, Texas, Mỹ, mất nhiều m&aacute;u, g&atilde;y xương sống. Barker cho hay c&ocirc; rời th&acirc;n x&aacute;c trong suốt cuộc phẫu thuật, tr&ocirc;i lơ lửng gần trần nh&agrave; v&agrave; nh&igrave;n xuống m&aacute;y theo d&otilde;i sự sống của ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p>Di chuyển dọc h&agrave;nh lang bệnh viện, c&ocirc; kể đ&atilde; nh&igrave;n thấy bố dượng đang vật lộn với nỗi đau buồn, &ocirc;ng đi mua một thanh kẹo từ m&aacute;y b&aacute;n h&agrave;ng tự động. H&agrave;nh động xuất ph&aacute;t v&igrave; căng thẳng qu&aacute; độ n&agrave;y bố dượng c&ocirc; chưa bao giờ kể với ai. Đ&acirc;y l&agrave; l&yacute; do khiến Barker thực sự tin những g&igrave; m&igrave;nh trải qua l&agrave; c&oacute; thật. Gắn b&oacute; với c&ocirc;ng việc gi&aacute;o sư viết s&aacute;ng tạo, c&ocirc; cho biết m&igrave;nh vẫn được những linh hồn đồng h&agrave;nh ở thế giới b&ecirc;n kia chỉ đường.</p>\r\n\r\n<p>3:Ashlee Barnett,sinh vi&ecirc;n cao đẳng:</p>\r\n\r\n<p>Ashlee Barnett đang l&agrave; sinh vi&ecirc;n cao đẳng khi c&ocirc; gặp tai nạn xe hơi tr&ecirc;n con đường cao tốc vắng vẻ ở bang Texas, Mỹ. Vỡ khung xương chậu, dập l&aacute; l&aacute;ch v&agrave; chảy m&aacute;u rất nhiều, Ashlee đứng giữa ranh giới hai thế giới: một b&ecirc;n của hoảng loạn v&agrave; đau đớn khi c&aacute;c b&aacute;c sĩ đang nỗ lực cứu sống c&ocirc; v&agrave; một b&ecirc;n c&oacute; &aacute;nh s&aacute;ng trắng l&oacute;a, kh&ocirc;ng c&ograve;n đớn đau, sợ h&atilde;i.</p>\r\n\r\n<p>Nhiều năm sau vụ tai nạn &aacute;m ảnh, Barnett ph&aacute;t hiện mắc ung thư, song trải nghiệm cận tử khiến c&ocirc; lu&ocirc;n tự tin rằng m&igrave;nh sẽ sống. Ashlee c&oacute; ba con, c&ocirc;ng việc của c&ocirc; l&agrave; tư vấn cho những người sống s&oacute;t gặp sang chấn t&acirc;m l&yacute;.</p>\r\n\r\n<p>4:David Bennett, kỹ thuật trưởng một con t&agrave;u:</p>\r\n\r\n<p>Ngo&agrave;i khơi California một đ&ecirc;m năm 1983, David Bennett, kỹ thuật trưởng một con t&agrave;u nghi&ecirc;n cứu c&ugrave;ng thủy thủ đo&agrave;n cố gắng chạy tho&aacute;t cơn b&atilde;o lớn tr&ecirc;n chiếc thuyền bơm hơi.</p>\r\n\r\n<p>C&aacute;ch bờ hơn 1,6 km, con thuyền nhỏ bị nhấn ch&igrave;m bởi ngọn s&oacute;ng cao tới 9 m. Tất cả bị hất văng xuống Th&aacute;i B&igrave;nh Dương lạnh c&oacute;ng. &Aacute;o phao hỏng, Bennett ch&igrave;m xuống biển, cảm gi&aacute;c nước tr&agrave;n v&agrave;o đầy phổi.</p>\r\n\r\n<p>Ngấp ngh&eacute; cửa tử thần, Bennett cho biết l&uacute;c đ&oacute; cảm thấy rất h&acirc;n hoan. Bennett c&ograve;n kể về tiếng n&oacute;i vang l&ecirc;n bảo rằng đ&acirc;y chưa phải l&agrave; thời điểm của &ocirc;ng. 18 ph&uacute;t sau khi ch&igrave;m xuống biển, Bennett nổi l&ecirc;n tr&ecirc;n mặt nước một c&aacute;ch thần kỳ khiến cả đo&agrave;n sửng sốt.</p>\r\n\r\n<p>Theo&nbsp;<em>National Geographic</em>, c&aacute;c h&igrave;nh ảnh trong trải nghiệm cận tử, theo một số nh&agrave; khoa học, chỉ đơn giản l&agrave; sự phản chiếu c&aacute;c hoạt động trong n&atilde;o do ảnh hưởng của t&igrave;nh trạng thiếu oxy đột ngột.</p>\r\n\r\n<p>Số kh&aacute;c lại tin rằng, đ&acirc;y c&oacute; lẽ l&agrave; những g&igrave; con người thực sự đối mặt sau khi chết. Tranh luận về đề t&agrave;i n&agrave;y vẫn tiếp diễn với nhiều m&acirc;u thuẫn v&agrave; đặt ra nhiều th&aacute;ch thức với giới khoa học. Tuy nhi&ecirc;n, điểm chung kh&oacute; tranh c&atilde;i ở những người đ&atilde; đứng tr&ecirc;n lằn ranh sinh tử ch&iacute;nh l&agrave; khi trở lại họ đều nhận thức r&otilde; hơn mục đ&iacute;ch sống của m&igrave;nh. &nbsp;</p>\r\n', 'public/images/2016041804474920160418044559c t.jpg', 'S', 3, '2016-04-18', 'Những người cận tử kể lại ký ức sống động về ánh sáng chói lòa, cảm giác hồn lìa khỏi xác, sự thanh thản, bình yên lạ lùng hay gặp gỡ những người thân đã khuất.'),
(78, 'Thế giới mất diện tích rừng bằng 1.000 sân bóng mỗi giờ', '<p>Theo&nbsp;<em>Telegraph</em>, thế giới đ&atilde; mất đi diện t&iacute;ch rừng tương đương 1.000 s&acirc;n b&oacute;ng đ&aacute; mỗi giờ trong 25 năm qua. C&aacute;c chuy&ecirc;n gia cảnh b&aacute;o vấn nạn ph&aacute; rừng l&agrave; một vấn đề lớn đối với thế giới do t&agrave;i nguy&ecirc;n rừng đang bị cạn kiệt nhanh ch&oacute;ng.</p>\r\n\r\n<p>Sau ng&agrave;y Quốc tế về Rừng 21/3 năm nay, một nghi&ecirc;n cứu cho biết ước t&iacute;nh hiện c&ograve;n 3.000 tỷ c&acirc;y xanh tr&ecirc;n Tr&aacute;i Đất.&nbsp;Tuy nhi&ecirc;n, theo số liệu c&ocirc;ng bố của Ng&acirc;n h&agrave;ng Thế giới, Tr&aacute;i Đất đ&atilde; mất 1,3 triệu km2 rừng từ năm 1990 tới nay, lớn hơn diện t&iacute;ch quốc gia Nam Phi.</p>\r\n\r\n<p>Trong khi v&ugrave;ng Trung Đ&ocirc;ng v&agrave; Bắc Phi c&oacute; tỷ lệ gia tăng diện t&iacute;ch rừng lớn nhất từ năm 1990 đến 2015, th&igrave; khu vực Mỹ Latin, Caribe v&agrave; tiểu v&ugrave;ng Sahara ch&acirc;u Phi mất nhiều diện t&iacute;ch rừng nhất, mỗi khu vực giảm 10%.</p>\r\n\r\n<p>Khu vực Mỹ Latin v&agrave; Caribe c&oacute; diện t&iacute;ch rừng sụt giảm nhiều nhất, 970.000 km2 từ năm 1990 đến 2015. V&ugrave;ng n&agrave;y c&oacute; diện t&iacute;ch rừng lớn thứ hai tr&ecirc;n thế giới, chiếm 1/4 tổng diện t&iacute;ch rừng to&agrave;n cầu.</p>\r\n\r\n<p>Đến năm 2012, hơn 14% diện t&iacute;ch đất tr&ecirc;n thế giới được c&aacute;c quốc gia bảo vệ.&nbsp;Mỹ Latin v&agrave; khu vực Caribe dẫn đầu tỷ lệ n&agrave;y, với 21,2% tổng diện t&iacute;ch đất được bảo vệ.</p>\r\n\r\n<p>&quot;Rừng đ&oacute;ng vai tr&ograve; ch&iacute;nh trong cuộc chiến chống đ&oacute;i ngh&egrave;o ở n&ocirc;ng th&ocirc;n, đảm bảo an ninh lương thực v&agrave; tạo c&ocirc;ng ăn việc l&agrave;m. V&agrave; rừng cung cấp c&aacute;c dịch vụ m&ocirc;i trường quan trọng như nước v&agrave; kh&ocirc;ng kh&iacute; sạch, bảo tồn đa dạng sinh học v&agrave; chống biến đổi kh&iacute; hậu&quot;, Jos&eacute; Graziano da Silva, Tổng gi&aacute;m đốc Tổ chức N&ocirc;ng lương Li&ecirc;n Hợp Quốc cho biết.</p>\r\n\r\n<p>&quot;Hướng thay đổi đang t&iacute;ch cực, nhưng con người cần l&agrave;m tốt hơn. Thế giới sẽ kh&ocirc;ng giảm thiểu được t&aacute;c động của biến đổi kh&iacute; hậu v&agrave; th&uacute;c đẩy ph&aacute;t triển bền vững nếu kh&ocirc;ng bảo vệ rừng v&agrave; sử dụng nhiều nguồn t&agrave;i nguy&ecirc;n bền vững m&agrave; rừng mang lại&quot;, &ocirc;ng Graziano nhấn mạnh.</p>\r\n', 'public/images/20160418045233rung.jpg', 'S', 3, '2016-04-18', 'Theo số liệu công bố của Ngân hàng Thế giới, khu vực Mỹ Latin và Caribe có diện tích rừng sụt giảm nhiều nhất thế giới từ năm 1990 tới nay.'),
(79, 'Điều gì xảy ra khi Mặt Trời tàn lụi và nuốt chửng Trái Đất', '<p>Theo&nbsp;<em>Live Science</em>, một ng&ocirc;i sao bắt đầu h&igrave;nh th&agrave;nh với sự t&iacute;ch tụ c&aacute;c loại kh&iacute;, chủ yếu l&agrave; hydro c&ugrave;ng heli v&agrave; một số nguy&ecirc;n tố kh&aacute;c. C&aacute;c ph&acirc;n tử kh&iacute; c&oacute; khối lượng, n&ecirc;n nếu c&oacute; nhiều kh&iacute; t&iacute;ch tụ lại với nhau, ch&uacute;ng sẽ sụp đổ dưới ch&iacute;nh lực hấp dẫn của ch&uacute;ng.</p>\r\n\r\n<p>Qu&aacute; tr&igrave;nh n&agrave;y tạo n&ecirc;n &aacute;p lực hướng v&agrave;o trong của một ng&ocirc;i sao nguy&ecirc;n thủy (protostar), đốt n&oacute;ng kh&iacute; tới mức c&aacute;c nguy&ecirc;n tử kh&iacute; chỉ c&ograve;n tồn tại dưới dạng plasma. Sau đ&oacute;, cứ hai hạt nh&acirc;n hydro sẽ kết hợp với nhau trong phản ứng nhiệt hạch, tạo th&agrave;nh một nguy&ecirc;n tử heli, giải ph&oacute;ng ra năng lượng dưới dạng nhiệt v&agrave; &aacute;nh s&aacute;ng, đồng thời tạo ra một &aacute;p lực hướng ra ph&iacute;a ngo&agrave;i, ngăn cản qu&aacute; tr&igrave;nh sụp đổ tiếp diễn.</p>\r\n\r\n<p>Phản ứng nhiệt hạch tạo ra heli n&agrave;y sẽ k&eacute;o d&agrave;i trong h&agrave;ng tỷ năm. Tới thời điểm gần hết nhi&ecirc;u liệu hydro trong l&otilde;i ng&ocirc;i sao, n&oacute; kh&ocirc;ng c&ograve;n ph&aacute;t ra nhiều năng lượng nữa, v&agrave; bắt đầu sụp đổ dưới trọng lượng của ch&iacute;nh n&oacute;. Tuy nhi&ecirc;n qu&aacute; tr&igrave;nh sụp đổ l&uacute;c n&agrave;y kh&ocirc;ng c&ograve;n đủ &aacute;p lực để kết hợp c&aacute;c hạt nh&acirc;n heli như hydro l&uacute;c trước. Ng&ocirc;i sao vẫn sẽ tỏa s&aacute;ng l&agrave; nhờ số &iacute;t hydro tr&ecirc;n bề mặt l&otilde;i vẫn tiếp tục phản ứng sinh ra năng lượng.</p>\r\n\r\n<p>L&otilde;i heli của ng&ocirc;i sao trong qu&aacute; tr&igrave;nh sụp đổ kh&ocirc;ng đủ năng lượng để g&acirc;y ra phản ứng kết hợp hạt nh&acirc;n như trước, nhưng do đ&acirc;y l&agrave; qu&aacute; tr&igrave;nh n&eacute;n kh&iacute; n&ecirc;n nhiệt độ tiếp tục tăng. Ng&ocirc;i sao l&uacute;c n&agrave;y thậm ch&iacute; c&ograve;n giải ph&oacute;ng nhiều năng lượng dưới dạng nhiệt v&agrave; &aacute;nh s&aacute;ng hơn. Tuy nhi&ecirc;n, năng lượng cũng l&agrave;m ng&ocirc;i sao nở to ra, trở th&agrave;nh sao khổng lồ đỏ. M&agrave;u đỏ c&oacute; nghĩa l&agrave; nhiệt độ bề mặt của n&oacute; thấp hơn giai đoạn phản ứng hạt nh&acirc;n l&uacute;c trước. Mặt Trời hiện nay đang ph&aacute;t ra &aacute;nh s&aacute;ng trắng, nhiệt độ cao hơn rất nhiều so với &aacute;nh s&aacute;ng đỏ.</p>\r\n\r\n<p>Một nghi&ecirc;n cứu v&agrave;o năm 2008 của hai nh&agrave; thi&ecirc;n văn Klaus-Peter Schr&ouml;der v&agrave; Robert Connon Smith ước t&iacute;nh khi trở th&agrave;nh sao lửa đỏ khổng lồ, bề mặt Mặt Trời sẽ mở rộng ra khoảng 170 triệu km, nuốt chửng sao Thủy, sao Kim v&agrave; Tr&aacute;i Đất. Qu&aacute; tr&igrave;nh trở th&agrave;nh sao khổng lồ của Mặt Trời sẽ k&eacute;o d&agrave;i khoảng 5 triệu năm.</p>\r\n\r\n<p><img alt="" src="/public/images/images/mts.jpg" style="height:306px; width:500px" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n, Mặt Trời đang s&aacute;ng th&ecirc;m khoảng 10% mỗi một tỷ năm. Do đ&oacute; v&ugrave;ng c&oacute; thể tồn tại sự sống quanh Mặt Trời, tức l&agrave; khoảng c&aacute;ch th&iacute;ch hợp cho c&aacute;c h&agrave;nh tinh tồn tại nước lỏng tr&ecirc;n bề mặt cũng sẽ được mở rộng.</p>\r\n\r\n<p>V&ugrave;ng n&agrave;y hiện nằm ở khoảng c&aacute;ch giữa 0,95 v&agrave; 1,37 lần b&aacute;n k&iacute;nh quỹ đạo Tr&aacute;i Đất (hay c&ograve;n gọi l&agrave; đơn vị thi&ecirc;n văn AU). Khi Mặt Trời trở th&agrave;nh sao&nbsp;đỏ&nbsp;khổng lồ, sao Hỏa sẽ ở trong v&ugrave;ng c&oacute; sự sống tại một thời điểm nhất định. Tr&aacute;i Đất l&uacute;c n&agrave;y sẽ nằm ngo&agrave;i v&ugrave;ng c&oacute; sự sống v&agrave; sẽ bị &quot;nướng&quot;, c&aacute;c đại dương bốc hơi, nước ph&acirc;n hủy hết th&agrave;nh oxy v&agrave; hydro.</p>\r\n\r\n<p>Hydro nhẹ n&ecirc;n sẽ tho&aacute;t ra ngo&agrave;i kh&ocirc;ng gian, c&ograve;n oxy sẽ phản ứng với đ&aacute; tr&ecirc;n bề mặt Tr&aacute;i Đất. Nitơ v&agrave; CO2 sẽ l&agrave; c&aacute;c th&agrave;nh phần chủ yếu trong kh&iacute; quyển, giống như sao Kim hiện nay. Hậu duệ của con người khi đ&oacute; sẽ phải di cư l&ecirc;n sao Hỏa hoặc t&igrave;m sự sống b&ecirc;n ngo&agrave;i Th&aacute;i Dương hệ.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, sao Hỏa cũng chỉ nằm trong v&ugrave;ng c&oacute; sự sống một thời gian. Tới khi Mặt Trời trở th&agrave;nh sao khổng lồ, v&ugrave;ng c&oacute; sự sống sẽ nằm trong khoảng 49&ndash;70 đơn vị thi&ecirc;n văn AU. Sao Hải Vương với quỹ đạo hiện nay c&oacute; thể sẽ trở n&ecirc;n qu&aacute; n&oacute;ng, kh&ocirc;ng thể sống được. Nơi th&iacute;ch hợp c&oacute; thể l&agrave; Pluto hoặc c&aacute;c h&agrave;nh tinh l&ugrave;n, sao chổi v&agrave; c&aacute;c tiểu h&agrave;nh tinh nhiều băng trong v&agrave;nh đai Kuiper.</p>\r\n\r\n<p>Một hiệu ứng được Schr&ouml;der v&agrave; Smith lưu &yacute; l&agrave; c&aacute;c ng&ocirc;i sao như Mặt Trời mất dần khối lượng theo thời gian, chủ yếu th&ocirc;ng qua ph&aacute;t t&aacute;n gi&oacute; Mặt Trời. Do đ&oacute;, quỹ đạo c&aacute;c h&agrave;nh tinh sẽ dần dần mở rộng. Quỹ đạo Tr&aacute;i Đất sẽ mở rộng kh&ocirc;ng đủ nhanh để tr&aacute;nh khỏi diệt vong nhưng sao Hải Vương c&oacute; thể sẽ trở th&agrave;nh ng&ocirc;i nh&agrave; mới cho lo&agrave;i người.</p>\r\n\r\n<p>Sau khi trở th&agrave;nh sao khổng lồ, Mặt Trời sẽ phải trải qua qu&aacute; tr&igrave;nh sụp đổ lần hai v&agrave; lại g&acirc;y ra phản ứng tổng hợp hạt nh&acirc;n. Trong khoảng hai tỷ năm, Mặt Trời sẽ tổng hợp carbon v&agrave; oxy từ heli, nhưng qu&aacute; tr&igrave;nh tổng hợp n&agrave;y kh&ocirc;ng giải ph&oacute;ng nhiều năng lượng như trước. Tới khi hết heli, kh&ocirc;ng c&ograve;n g&igrave; c&oacute; thể ngăn cản qu&aacute; tr&igrave;nh co lại của Mặt Trời nữa. L&otilde;i của n&oacute; sẽ thu lại th&agrave;nh một sao l&ugrave;n trắng. Li&ecirc;n kết giữa phần l&otilde;i v&agrave; phần vỏ ngo&agrave;i sẽ rất yếu, phần vỏ sẽ trở th&agrave;nh đ&aacute;m m&acirc;y h&agrave;nh tinh.</p>\r\n\r\n<p>Do sao l&ugrave;n trắng được nung n&oacute;ng nhờ qu&aacute; tr&igrave;nh n&eacute;n kh&iacute; hơn l&agrave; phản ứng nhiệt hạch, nhiệt độ của n&oacute; sẽ rất cao, với nhiệt độ bề mặt c&oacute; thể đạt tới 28.000 độ C v&agrave; n&oacute; sẽ thắp s&aacute;ng v&ugrave;ng kh&iacute; tinh v&acirc;n.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, thời gian cho tới khi Mặt Trời trở th&agrave;nh sao khổng lồ đỏ c&ograve;n rất d&agrave;i, &iacute;t nhất l&agrave; 7 đến 8 tỷ năm nữa. Con người mới chỉ sống trong 40 phần ngh&igrave;n của tổng qu&atilde;ng thời gian đ&oacute;. Nếu t&iacute;nh tuổi của Tr&aacute;i Đất l&agrave; 24 giờ th&igrave; con người mới chỉ hiện diện trong tối đa l&agrave; một gi&acirc;y.</p>\r\n', 'public/images/20160418045952mt.jpg', 'S', 3, '2016-04-18', 'Trước khi Mặt Trời nở to hết cỡ và biến thành sao đỏ khổng lồ nuốt chửng Trái Đất, hậu duệ của loài người có thể đã di cư tới những hành tinh khác như sao Hải Vương.'),
(80, 'Trung Quốc đứng đầu thế giới về năng lượng sạch', '<p>Lượng kh&iacute; thải m&agrave; Trung Quốc tạo ra c&oacute; nguy cơ đạt đỉnh v&agrave;o năm 2030, tuy nhi&ecirc;n theo b&aacute;o c&aacute;o của Viện nghi&ecirc;n cứu biến đổi kh&iacute; hậu Grantham v&agrave; Đại học Kinh tế v&agrave; Khoa học ch&iacute;nh trị London, dữ liệu trong năm 2014 v&agrave; ba qu&yacute; đầu năm 2015 cho thấy sự thay đổi trong bức tranh kinh tế v&agrave; năng lượng của nước n&agrave;y.</p>\r\n\r\n<p>Nguy&ecirc;n nh&acirc;n l&agrave; sự tăng trưởng nhanh ch&oacute;ng của Trung Quốc vốn ti&ecirc;u thụ năng lượng khổng lồ v&agrave; ph&aacute;t thải lượng kh&iacute; nh&agrave; k&iacute;nh kỷ lục đang chậm lại.</p>\r\n\r\n<p>M&ocirc; h&igrave;nh kinh tế của Trung Quốc trong những thập kỷ gần đ&acirc;y, giống như nhiều quốc gia đang ph&aacute;t triển kh&aacute;c, ch&uacute; trọng đầu tư v&agrave;o x&acirc;y dựng v&agrave; những ng&agrave;nh c&ocirc;ng nghiệp li&ecirc;n quan như th&eacute;p, xi măng, để mở rộng cơ sở hạ tầng. Những ng&agrave;nh n&agrave;y ti&ecirc;u thụ nhiều năng lượng, trong khi ở Trung Quốc, nguồn năng lượng chủ yếu lấy từ than đ&aacute;, loại nguy&ecirc;n liệu ph&aacute;t thải kh&iacute; nh&agrave; k&iacute;nh cực lớn. &nbsp;</p>\r\n\r\n<p>Hiện nay, việc đầu tư v&agrave;o cơ sở hạ tầng ở Trung Quốc đang chậm lại, nhu cầu sử dụng th&eacute;p v&agrave; xi măng giảm. Nước n&agrave;y cũng đang tăng cường đầu tư v&agrave;o thuỷ điện, năng lượng nguy&ecirc;n tử, gi&oacute; v&agrave; năng lượng Mặt Trời.</p>\r\n\r\n<p>Theo b&aacute;o c&aacute;o của Grantham, cuối năm 2014, năng lượng t&aacute;i tạo chiếm 11% tổng ti&ecirc;u thụ năng lượng ở Trung Quốc. Mức sử dụng than đ&aacute; kh&ocirc;ng tăng trong năm 2014 v&agrave; giảm v&agrave;o năm 2015.</p>\r\n\r\n<p>Theo Cơ quan Năng lượng Quốc tế (IEA), sự gia tăng trong sản xuất năng lượng t&aacute;i tạo ở Trung Quốc dự kiến cao hơn cả Li&ecirc;n minh ch&acirc;u &Acirc;u, Mỹ v&agrave; Nhật cộng lại.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ ri&ecirc;ng Trung Quốc, tại Mỹ, Cơ quan Th&ocirc;ng tin Năng lượng Quốc gia cho biết, trong năm tới, c&ocirc;ng suất điện Mặt Trời ở nước n&agrave;y sẽ lớn hơn tổng c&ocirc;ng suất điện từ kh&iacute; tự nhi&ecirc;n, gi&oacute; v&agrave; dầu mỏ.</p>\r\n\r\n<p>Trung Quốc v&agrave; Mỹ đang l&agrave; hai nước xả nhiều kh&iacute; thải nhất. Thoả thuận giữa hai nước năm 2014 về việc giảm ph&aacute;t thải kh&iacute; nh&agrave; k&iacute;nh đ&atilde; tạo ra một sự thay đổi lớn trong nỗ lực ngăn chặn biến đổi kh&iacute; hậu.</p>\r\n\r\n<p>Hai nước n&agrave;y cam kết sẽ giảm thiểu kh&iacute; thải trong v&ograve;ng 10-15 năm, v&agrave; sẽ tiếp tục nỗ lực cắt giảm trong tương lai. Đ&acirc;y l&agrave; cơ sở để c&aacute;c nước kh&aacute;c tham gia v&agrave;o Thoả thuận chung Paris sắp được k&yacute; kết tại trụ sở Li&ecirc;n Hợp Quốc ở New York trong th&aacute;ng n&agrave;y. Nếu tr&ecirc;n 55 nước tham gia k&yacute; kết, tương ứng với tối thiểu 55% tổng lượng ph&aacute;t thải kh&iacute; nh&agrave; k&iacute;nh, thoả thuận sẽ c&oacute; hiệu lực.</p>\r\n', 'public/images/20160418050241tqnls.jpg', 'S', 3, '2016-04-18', 'Từ quốc gia xả nhiều khí nhà kính nhất, Trung Quốc đang tiên phong trong việc đầu tư vào năng lượng tái tạo, dự kiến cao hơn cả Liên minh châu Âu, Mỹ và Nhật cộng lại.'),
(81, 'Ông Trần Đăng Tuấn: Tôi có nhiều cách đóng góp cho xã hội', '<p>Chia sẻ với&nbsp;<em>VnExpress</em>&nbsp;ng&agrave;y 17/4, nh&agrave; b&aacute;o Trần Đăng Tuấn cho hay, qua b&aacute;o ch&iacute; &ocirc;ng được biết Hội nghị Hiệp thương lần 3 do Mặt trận Tổ quốc th&agrave;nh phố H&agrave; Nội tổ chức đ&atilde; kh&ocirc;ng đưa phần lớn người tự ứng cử v&agrave;o danh s&aacute;ch bầu đại biểu Quốc hội kho&aacute; 14. &Ocirc;ng nằm trong danh s&aacute;ch n&agrave;y.</p>\r\n\r\n<p>&Ocirc;ng chia sẻ &quot;kh&ocirc;ng b&igrave;nh luận, cũng kh&ocirc;ng quan t&acirc;m l&yacute; do&quot; khi số đ&ocirc;ng trong 83 người dự hội nghị đ&atilde; kh&ocirc;ng ủng hộ m&igrave;nh. &Ocirc;ng cho rằng theo thủ tục bầu cử hiện nay những người n&agrave;y &quot;c&oacute; quyền như vậy&quot;.</p>\r\n\r\n<p>Về khả năng tiếp tục tự ứng cử Quốc hội kh&oacute;a sau, &ocirc;ng bảo c&ograve;n phụ thuộc sức khỏe của m&igrave;nh v&agrave; sự tin tưởng của cử tri.&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n Facebook c&aacute; nh&acirc;n, &ocirc;ng Tuấn cho hay&nbsp;đ&atilde; chuẩn bị kế hoạch trong trường hợp tr&uacute;ng cử để g&oacute;p phần nhỏ b&eacute; v&agrave;o th&uacute;c đẩy, gi&aacute;m s&aacute;t c&aacute;c lĩnh vực: an to&agrave;n thực phẩm, hiệu quả sử dụng nguồn lực hỗ trợ người ngh&egrave;o, v&ugrave;ng ngh&egrave;o, bảo vệ quyền lợi của c&aacute; nh&acirc;n v&agrave; cộng đồng bị ảnh hưởng hoặc bị yếu thế trong qu&aacute; tr&igrave;nh biến động ph&aacute;t triển kinh tế, vai tr&ograve; lớn hơn của b&aacute;o ch&iacute; v&agrave; của truyền th&ocirc;ng trong x&atilde; hội...</p>\r\n\r\n<p>&Ocirc;ng bảo c&oacute; phần nuối tiếc khi&nbsp;&quot;kh&ocirc;ng c&oacute; những điều kiện m&agrave; tư c&aacute;ch Đại biểu Quốc hội đem lại&quot; để thực hiện d&ugrave; chỉ một phần những c&ocirc;ng việc kể tr&ecirc;n. Tuy nhi&ecirc;n &ocirc;ng vẫn c&oacute; nhiều c&aacute;ch đ&oacute;ng g&oacute;p cho x&atilde; hội với tư c&aacute;ch một c&ocirc;ng d&acirc;n. &Ocirc;ng cũng&nbsp;gửi lời c&aacute;m ơn&nbsp;đến tất cả cử tri nơi m&igrave;nh cư tr&uacute;, c&ocirc;ng t&aacute;c v&agrave; những người ủng hộ &ocirc;ng khi gặp gỡ trực tiếp hoặc qua mạng x&atilde; hội trong thời gian qua.</p>\r\n\r\n<p>Ng&agrave;y 12/3, &ocirc;ng Tuấn nộp hồ sơ tự ứng cử đại biểu Quốc hội tại điểm tiếp nhận của Ủy ban bầu cử th&agrave;nh phố H&agrave; Nội. &Ocirc;ng chia sẻ l&yacute; do duy nhất tự ứng cử bởi &ldquo;nếu l&agrave; đại biểu Quốc hội sẽ c&oacute; nhiều điều kiện để l&agrave;m những điều đ&uacute;ng, điều hữu &iacute;ch cho cộng đồng hiệu quả hơn&rdquo;.&nbsp;</p>\r\n\r\n<p>Tại hội nghị hiệp thương lần 3 vừa qua, &ocirc;ng nằm trong số 46/48 người ứng cử đại biểu Quốc hội tại th&agrave;nh phố H&agrave; Nội bị loại khỏi danh s&aacute;ch bầu.</p>\r\n\r\n<p>&Ocirc;ng Tuấn, 59 tuổi, qu&ecirc; Nam Định, l&agrave; tiến sĩ chuy&ecirc;n ng&agrave;nh truyền h&igrave;nh được đ&agrave;o tạo tại Đại học Tổng hợp Lomonosov v&agrave; Viện H&agrave;n l&acirc;m Khoa học ở Li&ecirc;n X&ocirc;.&nbsp;&Ocirc;ng đảm nhiệm nhiều vị tr&iacute; tại Đ&agrave;i Truyền h&igrave;nh Việt Nam sau hơn 20 năm gắn b&oacute;, từ bi&ecirc;n tập vi&ecirc;n, b&igrave;nh luận vi&ecirc;n thời sự quốc tế đến Ph&oacute; tổng gi&aacute;m đốc.</p>\r\n\r\n<p>Cuối năm 2010, &ocirc;ng g&acirc;y bất ngờ khi l&agrave; người hiếm hoi xin nghỉ việc ở tuổi 53 tại vị tr&iacute; mang h&agrave;m tương đương thứ trưởng. Chia sẻ với&nbsp;<em>VnExpress</em>v&agrave;o&nbsp;thời điểm vừa &quot;từ quan&quot;, &ocirc;ng bảo: &quot;Ở tuổi của t&ocirc;i, tiền t&agrave;i, địa vị kh&ocirc;ng c&ograve;n qu&aacute; hấp dẫn. Điều t&ocirc;i muốn l&agrave; được trải nghiệm những g&igrave; m&igrave;nh chưa c&oacute; trong mấy chục năm qua&quot;.</p>\r\n\r\n<p>&Ocirc;ng sau đ&oacute; trở th&agrave;nh tổng gi&aacute;m đốc c&ocirc;ng ty tư nh&acirc;n trong lĩnh vực sản xuất truyền h&igrave;nh. Những năm gần đ&acirc;y, &ocirc;ng được biết đến l&agrave; người t&iacute;ch cực tổ chức chương tr&igrave;nh &quot;Cơm c&oacute; thịt&quot; gi&uacute;p đỡ&nbsp;h&agrave;ng vạn trẻ em ngh&egrave;o v&ugrave;ng cao. Dự &aacute;n g&acirc;y tiếng vang trong cộng đồng, quy tụ nhiều người tham gia v&igrave; những việc l&agrave;m thiết thực lan tỏa t&igrave;nh nh&acirc;n &aacute;i.</p>\r\n', 'public/images/20160418050806pl.jpg', 'P', 3, '2016-04-18', 'Không có tên trong danh sách ứng viên đại biểu Quốc hội sau hội nghị hiệp thương, nhà báo tự ứng cử Trần Đăng Tuấn cho biết không quan tâm lý do, không bất ngờ.');
INSERT INTO `news` (`idnews`, `title`, `content`, `image`, `categorynews`, `id`, `datepost`, `summary`) VALUES
(82, 'Tháng 7 Quốc hội bầu lại Chủ tịch nước, Thủ tướng', '<p><strong>Trả lời c&acirc;u hỏi về việc một số người được miễn nhiệm vừa qua &quot;thấy bị động v&igrave; th&ocirc;ng b&aacute;o muộn, ảnh hưởng đến kế hoạch c&ocirc;ng t&aacute;c&quot;,&nbsp;</strong>Tổng thư k&yacute; Nguyễn Hạnh Ph&uacute;c cho hay, kỳ họp n&agrave;y Quốc hội d&agrave;nh kh&aacute; nhiều thời gian để kiện to&agrave;n nh&acirc;n sự. Việc n&agrave;y kh&ocirc;ng phải bị động m&agrave; c&oacute; chuẩn bị, c&oacute; th&ocirc;ng b&aacute;o cho những người được miễn nhiệm từ trước.</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta thực hiện đ&uacute;ng theo quy tr&igrave;nh miễn nhiệm, bổ nhiệm c&aacute;n bộ. Quốc hội vừa qua thực hiện đ&uacute;ng quy tr&igrave;nh&quot;, &ocirc;ng Ph&uacute;c khẳng định v&agrave; n&oacute;i th&ecirc;m, lịch sử Quốc hội đ&atilde; c&oacute; nhiệm kỳ kiện to&agrave;n nh&acirc;n sự tương tự, như kho&aacute; 11 kiện to&agrave;n khoảng 9 chức danh kh&aacute;c nhau. Kỳ n&agrave;y kiện to&agrave;n 37 chức danh Quốc hội v&agrave; ph&ecirc; chuẩn chức danh của Ch&iacute;nh phủ.</p>\r\n\r\n<p>Việc Quốc hội kh&ocirc;ng miễn nhiệm chức vụ Ph&oacute; chủ tịch Quốc hội với b&agrave; Nguyễn Thị Kim Ng&acirc;n v&agrave; chức vụ Ph&oacute; thủ tướng với &ocirc;ng Nguyễn Xu&acirc;n Ph&uacute;c trước khi bầu nhiệm vụ mới, được &ocirc;ng Hạnh Ph&uacute;c l&yacute; giải, &quot;Quốc hội đ&atilde; bầu b&agrave; Ng&acirc;n l&agrave;m Chủ tịch Quốc hội, th&igrave; kh&ocirc;ng c&oacute; chuyện lại ki&ecirc;m Ph&oacute; chủ tịch, n&ecirc;n kh&ocirc;ng cần miễn nhiệm nữa. Tương tự, &ocirc;ng Nguyễn Xu&acirc;n Ph&uacute;c được bầu l&agrave;m Thủ tướng th&igrave; kh&ocirc;ng thể ki&ecirc;m nhiệm Ph&oacute; thủ tướng&quot;.</p>\r\n\r\n<p><strong>L&yacute; lịch c&aacute;c chức danh được bầu, ph&ecirc; chuẩn đều c&oacute; d&ograve;ng nhận x&eacute;t &quot;đ&atilde; ho&agrave;n th&agrave;nh c&ocirc;ng việc&quot; m&agrave; kh&ocirc;ng c&oacute; đ&aacute;nh gi&aacute; về đạo đức</strong>, nh&acirc;n c&aacute;ch, c&aacute;c phi&ecirc;n thảo luận lại l&agrave; k&iacute;n n&ecirc;n cử tri băn khoăn kh&ocirc;ng biết về năng lực nh&acirc;n sự,&nbsp;&ocirc;ng Nguyễn Hạnh Ph&uacute;c cho hay, qu&aacute; tr&igrave;nh thảo luận tại đo&agrave;n, c&aacute;c&nbsp;đại biểu quốc hội - đại diện cho d&acirc;n đ&atilde; c&oacute; qu&aacute; tr&igrave;nh nghi&ecirc;n cứu hồ sơ, đ&aacute;nh gi&aacute;, cho &yacute; kiến, sau đ&oacute; c&oacute; sự c&acirc;n nhắc, thể hiện quan điểm bằng l&aacute; phiếu. Khi c&ocirc;ng bố kết quả, c&oacute; ứng vi&ecirc;n đạt phiếu cao, c&oacute; người phiếu thấp, đ&oacute; l&agrave; sự đ&aacute;nh gi&aacute; c&ocirc;ng bằng của đại biểu.</p>\r\n\r\n<p>Theo Ph&oacute; chủ tịch Quốc hội U&ocirc;ng Chu Lưu, do nhu cầu c&ocirc;ng việc, sắp xếp bộ m&aacute;y của Đảng theo quy định của hiến ph&aacute;p, ph&aacute;p luật, Quốc hội kho&aacute; 13 kiện to&agrave;n 37 chức danh chủ chốt để đảm bảo sự đồng bộ giữa vai tr&ograve; l&atilde;nh đạo của Đảng với hiệu lực, hiệu quả của bộ m&aacute;y nh&agrave; nước.</p>\r\n\r\n<p>&quot;Nh&acirc;n sự vừa được Quốc hội bầu v&agrave; ph&ecirc; chuẩn l&agrave; của Kho&aacute; 13, nhiệm kỳ 2011-2016. Sau khi bầu cử Quốc hội Kho&aacute; 14 (ng&agrave;y 22/5), ch&uacute;ng ta lại kiện to&agrave;n nh&acirc;n sự của c&aacute;c cơ quan nh&agrave; nước&quot;, &ocirc;ng Lưu cho hay.</p>\r\n\r\n<p>Luật Tổ chức Quốc hội quy định c&aacute;c chức danh trong bộ m&aacute;y nh&agrave; nước bắt buộc phải l&agrave; đại biểu Quốc hội gồm Chủ tịch nước, Ph&oacute; chủ tịch nước, Thủ tướng, Chủ tịch Quốc hội, Ph&oacute; chủ tịch Quốc hội, ủy vi&ecirc;n Ủy ban thường vụ Quốc hội, Chủ tịch Hội đồng D&acirc;n tộc, Chủ nhiệm Ủy ban của Quốc hội.</p>\r\n\r\n<p>Như vậy, nếu c&aacute;c chức danh mới tr&uacute;ng cử sắp tới được bầu l&agrave; đại biểu Quốc hội th&igrave; sẽ tiếp tục được giới thiệu đảm nhiệm chức vụ, c&ograve;n người n&agrave;o kh&ocirc;ng tr&uacute;ng cử th&igrave; sẽ kết th&uacute;c nhiệm kỳ, được Đảng, Nh&agrave; nước bố tr&iacute; c&ocirc;ng việc kh&aacute;c m&agrave; kh&ocirc;ng bắt buộc phải l&agrave; đại biểu.</p>\r\n\r\n<p>Kỳ họp 11 Quốc hội kh&oacute;a 13 từ 20/3 đến 12/4 d&agrave;nh phần lớn thời gian kiện&nbsp;to&agrave;n nh&acirc;n sự Nh&agrave; nước.</p>\r\n\r\n<p>Theo đ&oacute;, Chủ tịch Quốc hội Nguyễn Sinh H&ugrave;ng, Chủ tịch nước Trương Tấn Sang, Thủ tướng Nguyễn Tấn Dũng đ&atilde; được miễn nhiệm. Những người được bầu kế nhiệm l&agrave; Chủ tịch Quốc hội Nguyễn Thị Kim Ng&acirc;n, Chủ tịch nước Trần Đại Quang, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c.</p>\r\n\r\n<p>H&agrave;ng loạt nh&acirc;n sự trong bộ m&aacute;y cũng được bầu mới gồm b&agrave;&nbsp;Đặng Thị Ngọc Thịnh thay b&agrave; Nguyễn Thị Doan giữ chức Ph&oacute; chủ tịch nước. &Ocirc;ng Nguyễn H&ograve;a B&igrave;nh (nguy&ecirc;n Viện trưởng viện kiểm s&aacute;t nh&acirc;n d&acirc;n tối cao) tr&uacute;ng cử l&agrave;m Ch&aacute;nh &aacute;n t&ograve;a &aacute;n nh&acirc;n d&acirc;n tối cao thay &ocirc;ng Trương Ho&agrave; B&igrave;nh. &Ocirc;ng L&ecirc; Minh Tr&iacute; (Ph&oacute; trưởng ban Nội ch&iacute;nh Trung ương) được bầu l&agrave;m Viện trưởng Viện kiểm s&aacute;t nh&acirc;n d&acirc;n tối cao thay &ocirc;ng Nguyễn Ho&agrave; B&igrave;nh.</p>\r\n\r\n<p>Ch&iacute;nh phủ cũng c&oacute; 21 th&agrave;nh vi&ecirc;n mới, trong đ&oacute; c&oacute; ba t&acirc;n Ph&oacute; thủ tướng v&agrave; 18 Bộ trưởng, trưởng ng&agrave;nh.&nbsp;</p>\r\n\r\n<p>7 dự &aacute;n luật được th&ocirc;ng qua l&agrave; Luật tiếp cận th&ocirc;ng tin; Luật k&yacute; kết, gia nhập v&agrave; thực hiện điều ước quốc tế (sửa đổi); Luật b&aacute;o ch&iacute; (sửa đổi); Luật bảo vệ, chăm s&oacute;c v&agrave; gi&aacute;o dục trẻ em (sửa đổi); Luật dược (sửa đổi); Luật sửa đổi, bổ sung một số điều của Luật thuế gi&aacute; trị gia tăng, Luật thuế ti&ecirc;u thụ đặc biệt v&agrave; Luật quản l&yacute; thuế; Luật thuế xuất khẩu, thuế nhập khẩu (sửa đổi).</p>\r\n', 'public/images/20160418051446ctn.jpg', 'P', 3, '2016-04-18', 'Kỳ họp đầu tiên Quốc hội khoá 14 dự kiến vào tháng 7, đại biểu sẽ bầu lại Chủ tịch nước, Thủ tướng, Chủ tịch Quốc hội cùng nhiều chức vụ'),
(83, 'Những vị vua trong sử Việt hạ mình nhận lỗi', '<p>L&ecirc; Th&aacute;nh T&ocirc;ng (1442-1497) l&agrave;&nbsp;vị vua thứ 5 của triều đại L&ecirc; Sơ,&nbsp;được đ&aacute;nh gi&aacute; l&agrave; &ocirc;ng vua s&aacute;ng suốt bậc nhất trong lịch sử nước ta. Với 38 năm &ocirc;ng trị v&igrave;, nước&nbsp;<em>Đại Việt</em>&nbsp;ph&aacute;t triển rực rỡ về mọi mặt từ kinh tế, văn h&oacute;a, x&atilde; hội, gi&aacute;o dục, qu&acirc;n sự v&agrave; trở th&agrave;nh một cường quốc.&nbsp;</p>\r\n\r\n<p>Theo c&aacute;c nh&agrave; sử học, ngo&agrave;i tư chất th&ocirc;ng minh, vua L&ecirc; Th&aacute;nh T&ocirc;ng rất ham học hỏi v&agrave; cầu thị. Thậm ch&iacute;, &ocirc;ng kh&ocirc;ng ngại nhận sai v&agrave; xin lỗi c&aacute;c bề t&ocirc;i. S&aacute;ch&nbsp;<em>Đại Việt sử k&yacute; to&agrave;n thư</em>&nbsp;viết, một lần Quốc tử Tế tửu gi&aacute;m ki&ecirc;m Văn minh điện Đại học sĩ l&agrave; Nguyễn B&aacute; K&yacute; thẳng thắn d&acirc;ng sớ về việc vua l&agrave;m văn chỉ chuộng lối học ph&ugrave; hoa, v&ocirc; dụng m&agrave;&nbsp;kh&ocirc;ng ch&uacute; &yacute; kinh, sử.</p>\r\n\r\n<p>Vua L&ecirc; Th&aacute;nh T&ocirc;ng kh&ocirc;ng để bụng m&agrave; trả lời rằng: &quot;Nếu ta ưa chuộng văn hoa, kh&ocirc;ng lấy gốc kinh, sử th&igrave; c&ograve;n s&aacute;ch n&agrave;o nữa? Ta tự x&eacute;t m&igrave;nh, x&eacute;t lời ngươi n&oacute;i th&igrave; trong bốn chữ &#39;ph&ugrave; hoa v&ocirc; dụng&#39; kia, thực đ&atilde; gồm cả l&ograve;ng trung rồi. Thế m&agrave; ta lại l&agrave;m văn m&agrave; biện lại, th&igrave; ta thực c&oacute; lỗi. L&uacute;c ấy người đ&atilde; kịp thời can ngăn, chẳng v&igrave; thế m&agrave; giữ &yacute;&quot;.</p>\r\n\r\n<p>Vua L&ecirc; Th&aacute;nh T&ocirc;ng v&igrave; qu&yacute; trọng t&agrave;i đức Nguyễn B&aacute; K&yacute; m&agrave; ban hiệu &quot;V&acirc;n Phong ti&ecirc;n sinh&rdquo;. Khi &ocirc;ng mất, vua sai Tư lễ gi&aacute;m quan Phạm Hổ đem sắc đến dụ rằng: &ldquo;Ngươi thờ vua trung th&agrave;nh, giữ m&igrave;nh ch&iacute;nh trực, sớm tối ở b&ecirc;n ta, nay được 6 năm, l&uacute;c thoi th&oacute;p rồi m&agrave; l&ograve;ng trung vẫn chưa th&ocirc;i&quot;.</p>\r\n\r\n<p>Một lần kh&aacute;c, năm 1467 ở v&ugrave;ng Đ&ocirc;ng Bắc c&oacute; loạn, Tổng binh L&ecirc; Hối v&agrave;&nbsp;Đ&ocirc; đốc Khuất Đả đem qu&acirc;n đi&nbsp;dẹp loạn đều bại trận n&ecirc;n bị đem ra x&eacute;t xử. Quan H&igrave;nh bộ thượng thư viện dẫn lệ b&aacute;t nghị (8 loại người được chiếu cố, nghị x&eacute;t giảm tội) c&oacute; &yacute; tha bổng cho hai người n&agrave;y.</p>\r\n\r\n<p>Quan Đ&ocirc; ngự sử l&agrave; Trần X&aacute;c t&aacute;n th&agrave;nh v&agrave; tấu với vua: &quot;Xưa nay chỉ c&oacute; tội đại &aacute;c v&agrave; phản nghịch l&agrave; kh&ocirc;ng được hưởng lệ b&aacute;t nghị, chưa từng thấy quan phạm tội thường m&agrave; kh&ocirc;ng cho hưởng lệ n&agrave;y&quot;. L&ecirc; Th&aacute;nh T&ocirc;ng trả lời rằng: &quot;Qu&acirc;n ph&aacute;p chỉ c&oacute; một chứ kh&ocirc;ng c&oacute; hai. N&oacute;i như lời của X&aacute;c chỉ l&agrave; biện b&aacute;c, m&ecirc; hoặc người m&agrave; th&ocirc;i, phải n&ecirc;n trị X&aacute;c về tội n&agrave;y mới được&quot;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, liền sau đ&oacute;, vua lại c&oacute; dụ nhận lỗi: &quot;Ta vu oan cho ngươi l&agrave; kẻ biện b&aacute;c để m&ecirc; hoặc người, đ&oacute; l&agrave; ta lỡ lời. Nay, người c&oacute; mưu kế g&igrave; hay cứ n&oacute;i với ta, ấy cũng như cơn mưa ngọt đến khi trời nắng hạn, như c&oacute; con thuyền đến l&uacute;c ta cần qua s&ocirc;ng. Người h&atilde;y k&iacute;nh nhớ lấy&quot;.</p>\r\n\r\n<p>C&aacute;c sử gia đ&aacute;nh gi&aacute;, Đ&ocirc; ngự sử Trần X&aacute;c l&agrave; người cương trực, nắm luật lệ rất chắc, d&aacute;m n&oacute;i những lời kh&ocirc;ng đẹp tai rồng. Trong l&uacute;c n&oacute;ng vội nhất thời, L&ecirc; Th&aacute;nh T&ocirc;ng kh&ocirc;ng giữ được ph&eacute;p xử thường nhưng vua c&ograve;n biết nghĩ lại, dũng cảm nhận lỗi v&agrave; th&agrave;nh khẩn xin nghe tiếp những lời thẳng thắn l&agrave; điều rất đ&aacute;ng k&iacute;nh.</p>\r\n\r\n<p><strong>Vua Quang Trung tự nhận sai</strong></p>\r\n\r\n<p>Quang Trung (1753-1792) l&agrave; vị vua c&oacute; nhiều chiến c&ocirc;ng&nbsp;lừng lẫy&nbsp;v&agrave; cũng được khen ngợi t&agrave;i ba s&aacute;ng suốt. Trong&nbsp;nhiều c&acirc;u chuyện n&oacute;i về t&agrave;i năng lỗi lạc của &ocirc;ng, c&oacute; sự việc cho thấy nh&agrave; vua rất cầu thị. Đ&oacute; l&agrave; việc ứng xử trước l&aacute; đơn &quot;kiện&quot; của d&acirc;n l&agrave;ng Văn Chương ở Thăng Long.</p>\r\n\r\n<p>Theo t&agrave;i liệu của nh&agrave; sử học Trần Văn Gi&aacute;p, năm 1789, sau khi vua Quang Trung đ&aacute;nh thắng qu&acirc;n Thanh th&igrave; d&acirc;n trại Văn Chương (ở gần Văn Miếu) d&acirc;ng l&ecirc;n một tờ sớ. Nội dung n&oacute;i về việc năm 1786, Văn miếu Thăng Long bị đốt ch&aacute;y, bia đ&aacute; bị đạp đổ ngổn ngang.</p>\r\n\r\n<p>Tờ đơn n&oacute;i r&otilde;:&nbsp;<em>&quot;</em><em>Bia Tiến sĩ v&ocirc; can v&ocirc; tội/ M&agrave; vạ l&acirc;y v&igrave; nỗi ch&aacute;y th&agrave;nh/ Bia th&igrave; đạp đổ tung ho&agrave;nh/ Nh&agrave; bia th&igrave; đốt tan t&agrave;nh ra tro&quot;.&nbsp;</em>Tờ đơn cũng c&oacute; &yacute; tr&aacute;ch vua Quang Trung:&nbsp;<em>&quot;</em><em>Một nền văn hiến l&acirc;u d&agrave;i/ Tiếc thay chưa được đ&oacute;n Ng&agrave;i ngự thăm&quot;.</em></p>\r\n\r\n<p>Đọc sớ, vua Quang Trung thấy lạ v&igrave; n&oacute; kh&ocirc;ng viết theo thể thức tờ sớ th&ocirc;ng thường m&agrave; được một thầy Nho viết bằng chữ N&ocirc;m văn vần theo đ&uacute;ng sở th&iacute;ch của &ocirc;ng.&nbsp;Mặt kh&aacute;c tờ sớ tuy dẫn ra nghi vấn qu&acirc;n Trịnh Khải ph&aacute; bia nhưng thực chất l&agrave; để n&oacute;i tr&aacute;nh việc qu&acirc;n T&acirc;y Sơn ph&aacute; Văn miếu nhằm đỡ l&agrave;m mất l&ograve;ng vua. Sớ c&ograve;n kh&ocirc;ng gọi vua l&agrave; ho&agrave;ng thượng m&agrave; lại lắt l&eacute;o gọi l&agrave; Ng&agrave;i, ngầm tỏ &yacute; chưa phục.</p>\r\n\r\n<p>Vua Quang Trung kh&ocirc;ng lờ đi hoặc sai người tra x&eacute;t xem kẻ n&agrave;o to gan viết tờ sớ đ&oacute; v&agrave; những ai dự v&agrave;o việc đ&ograve;i dựng lại bia m&agrave; vị ho&agrave;ng đế&nbsp;đ&atilde; ph&ecirc; ngay v&agrave;o đơn ấy:&nbsp;<em>Ta kh&ocirc;ng tr&aacute;ch n&ocirc;ng phu/ Ta chỉ gờm th&agrave;y Nho/ Cả gan to mật, d&aacute;m k&ecirc;u Vua bằng Ng&agrave;i! Th&agrave;y Nho l&agrave; ai? Sắc cho Bộ hỏi, d&acirc;n khai.</em></p>\r\n\r\n<p>Vua Quang Trung đ&atilde; kh&ocirc;ng hạch hỏi tiếp m&agrave; đi ngay v&agrave;o điều cốt yếu cần giải quyết, cụ thể l&agrave; cho ngay th&aacute;nh chỉ c&ugrave;ng ch&acirc;u khuy&ecirc;n v&agrave;o tờ đơn N&ocirc;m đ&oacute;:<em>&nbsp;&quot;Th&ocirc;i! Th&ocirc;i! Th&ocirc;i! Việc đ&atilde; rồi /Trăm điều h&atilde;y cứ tr&aacute;ch bồi v&agrave;o ta! Nay mai dọn lại nước nh&agrave;/ Bia Ngh&egrave; lại dựng tr&ecirc;n t&ograve;a mu&ocirc;n gian/Cơ đồ họ Trịnh đ&atilde; tan?/ Chớ đổ Trịnh Khải m&agrave; oan mu&ocirc;n đời&quot;.</em></p>\r\n\r\n<p>Theo c&aacute;c nh&agrave; sử học, trước đơn &quot;kiện&quot; của d&acirc;n, nh&agrave; vua chẳng quanh co m&agrave; thẳng thắn nhận tr&aacute;ch nhiệm v&agrave; hứa sẽ sửa chữa lại Văn miếu. Với việc n&agrave;y quả thực vua Quang Trung đ&atilde; thể hiện sự dũng cảm v&agrave; thẳng thắn của một bậc minh qu&acirc;n.</p>\r\n\r\n<p><strong>Vua L&yacute; Cao T&ocirc;ng ban chiếu hối lỗi</strong></p>\r\n\r\n<p>L&agrave; vị vua thứ 7 của triều đại nh&agrave; L&yacute;, Cao T&ocirc;ng (1173-1210) l&ecirc;n ng&ocirc;i khi chỉ mới 3 tuổi với sự trợ gi&uacute;p của Th&aacute;i &uacute;y T&ocirc; Hiến Th&agrave;nh. Lớn l&ecirc;n, khi trực tiếp cầm quyền trị nước, Cao T&ocirc;ng ham m&ecirc; săn bắn, ch&iacute;nh sự ph&aacute;p luật kh&ocirc;ng r&otilde; r&agrave;ng, vơ v&eacute;t của d&acirc;n x&acirc;y nhiều cung điện, bắt trăm họ phục dịch n&ecirc;n trộm cướp nổi l&ecirc;n khắp nơi.</p>\r\n\r\n<p>S&aacute;ch&nbsp;<em>Đại Việt sử k&yacute; to&agrave;n thư</em>&nbsp;ch&eacute;p: &quot;Vua chơi bời kh&ocirc;ng điều độ, h&igrave;nh ch&iacute;nh kh&ocirc;ng r&otilde; r&agrave;ng, giặc cướp như ong, đ&oacute;i k&eacute;m liền năm, cơ nghiệp nh&agrave; L&yacute; từ đấy suy k&eacute;m...&quot;.&nbsp;M&atilde;i đến năm 1207, nh&agrave; vua nh&igrave;n cảnh giặc cướp n&ecirc;n hối lỗi v&agrave; ban chiếu tự nhận lỗi lầm.</p>\r\n\r\n<p>S&aacute;ch&nbsp;<em>Đại Việt sử lược</em>&nbsp;ch&eacute;p lại bản chiếu đ&oacute;: &quot;Trẫm c&ograve;n b&eacute; m&agrave; phải g&aacute;nh v&aacute;c việc lớn, ở tận nơi cửu tr&ugrave;ng, kh&ocirc;ng biết được cảnh kh&oacute; khăn của d&acirc;n ch&uacute;ng, nghe lời bọn tiểu nh&acirc;n m&agrave; g&acirc;y n&ecirc;n o&aacute;n với kẻ dưới. D&acirc;n đ&atilde; o&aacute;n th&igrave; trẫm c&ograve;n biết dựa v&agrave;o ai? Nay trẫm sẽ sửa lỗi, c&ugrave;ng d&acirc;n đổi mới. Ai c&oacute; ruộng đất, sản nghiệp bị sung c&ocirc;ng sẽ được ho&agrave;n lại&quot;.</p>\r\n\r\n<p>Với chiếu hối lỗi, L&yacute; Cao T&ocirc;ng được xem l&agrave; vị vua đầu ti&ecirc;n trong lịch sử phong kiến Việt Nam &quot;d&aacute;m&quot; hạ m&igrave;nh xin lỗi d&acirc;n, song năm 1207 đ&atilde; l&agrave; những năm cuối đời của &ocirc;ng vua n&agrave;y. Ba năm sau, L&yacute; Cao T&ocirc;ng qua đời trong cảnh đất nước loạn lạc v&agrave; triều đ&igrave;nh phải dựa hẳn v&agrave;o thế lực nh&agrave; họ Trần. V&igrave; vậy, chiếu nhận lỗi của vị vua n&agrave;y được giới sử học xem như l&agrave; những lời hối lỗi qu&aacute; muộn m&agrave;ng v&agrave; hối chỉ để m&agrave; hối th&ocirc;i chứ kh&ocirc;ng thực hiện một biện ph&aacute;p g&igrave; để chấn chỉnh hoặc t&igrave;nh trạng đ&atilde; qu&aacute; tệ để c&oacute; thể thay đổi được.</p>\r\n', 'public/images/20160418051837sv.jpg', 'P', 3, '2016-04-18', 'Vua là tối thượng, được xem là thiên tử và luôn luôn đúng, song một số vị đã "dũng cảm" nhận lỗi với bề tôi khi thấy mình sai.'),
(84, 'Hơn 1.000 du khách mắc kẹt ở Cù Lao Chàm vì sóng lớn', '<p>Tối 18/4, &ocirc;ng Nguyễn Văn An, Chủ tịch UBND x&atilde; đảo T&acirc;n Hiệp (TP Hội An, Quảng Nam), cho biết đang c&oacute; hơn 1.100 du kh&aacute;ch trong nước v&agrave; nước ngo&agrave;i đang bị mắc kẹt tại đảo C&ugrave; Lao Ch&agrave;m kh&ocirc;ng v&agrave;o được đất liền v&igrave; s&oacute;ng to, gi&oacute; lớn, biển động rất mạnh.</p>\r\n\r\n<p><img alt="" src="/public/images/images/cl1.jpg" style="height:333px; width:500px" /></p>\r\n\r\n<p>Chủ tịch x&atilde; cho hay, s&aacute;ng nay t&agrave;u du lịch đưa c&aacute;c du kh&aacute;ch ra đảo thăm quan. C&aacute;c đo&agrave;n dự kiến quay về th&agrave;nh phố Hội An v&agrave;o buổi chiều nhưng bất ngờ gặp trời gi&ocirc;ng lốc, biển động mạnh khiến t&agrave;u kh&ocirc;ng thể rời cảng.</p>\r\n\r\n<p>&ldquo;Nếu s&aacute;ng mai biển lặng t&agrave;u mới xuất bến đưa du kh&aacute;ch v&agrave;o lại đất liền. Rất hiếm khi c&oacute; lượng du kh&aacute;ch lớn mắc kẹt như lần n&agrave;y&rdquo;, &ocirc;ng An n&oacute;i. Trước nguy cơ qu&aacute; tải nơi lưu tr&uacute;, tối nay x&atilde; sẽ nổ m&aacute;y ph&aacute;t điện để phục vụ, đồng thời sắp xếp cho du kh&aacute;ch nghỉ ở c&aacute;c nh&agrave; d&acirc;n v&agrave; một số c&ocirc;ng sở tr&ecirc;n đảo.</p>\r\n\r\n<p>&ldquo;Ch&uacute;ng t&ocirc;i cam kết sẽ kh&ocirc;ng để du kh&aacute;ch thiếu chỗ ngủ v&agrave; ăn uống. X&atilde; sẽ c&oacute; tr&aacute;ch nhiệm chăm lo, nhất l&agrave; sức khoẻ trẻ em, người lớn tuổi&rdquo;, &ocirc;ng An n&oacute;i.</p>\r\n', 'public/images/20160418052020clc.jpg', 'P', 3, '2016-04-18', 'Chiều 18/4 trời bất ngờ nổi giông lốc, biển động mạnh khiến tàu không thể xuất bến đưa hơn 1.000 du khách từ đảo Cù Lao Chàm (Quảng Nam) vào đất liền.'),
(86, 'Hơn 10.000 tỷ đồng đầu tư vào Quảng Trị', '<p>Ng&agrave;y 17/4, UBND tỉnh Quảng Trị v&agrave; Ng&acirc;n h&agrave;ng Đầu tư v&agrave; ph&aacute;t triển Việt Nam (BIDV) đ&atilde; tổ chức Hội nghị x&uacute;c tiến đầu tư v&agrave; quảng b&aacute; du lịch tỉnh, với sự tham gia v&agrave; chỉ đạo của Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c.</p>\r\n\r\n<p>Với diện t&iacute;ch tự nhi&ecirc;n khoảng 4.700 km2, d&acirc;n số khoảng 620.000 người, c&aacute;c đại biểu nhấn mạnh Quảng Trị cần kết hợp ph&aacute;t triển kinh tế với an ninh quốc ph&ograve;ng, cải thiện mạnh mẽ m&ocirc;i trường đầu tư, thực sự đồng h&agrave;nh c&ugrave;ng doanh nghiệp.</p>\r\n\r\n<p>Chủ tịch UBND tỉnh - Nguyễn Đức Ch&iacute;nh cam kết sẽ rộng cửa đ&oacute;n nh&agrave; đầu tư với thời hạn kh&ocirc;ng qu&aacute; 10 ng&agrave;y để cấp chứng nhận đầu tư, bố tr&iacute; quỹ đất sạch, đầu tư điện nước. L&atilde;nh đạo tỉnh cũng sẽ trực tiếp giải quyết kh&oacute; khăn của doanh nghiệp tr&ecirc;n đường d&acirc;y n&oacute;ng...</p>\r\n\r\n<p>Trong khi đ&oacute;, Thủ tướng Nguyễn Xu&acirc;n Ph&uacute;c cho rằng Quảng Trị c&oacute; thuận lợi về giao th&ocirc;ng, nằm tr&ecirc;n h&agrave;nh lang kinh tế Đ&ocirc;ng T&acirc;y, c&oacute; hệ thống di t&iacute;ch lịch sử v&agrave; truyền thống ki&ecirc;n trung của con người...&nbsp;&ldquo;Quảng Trị cần tạo thuận lợi nhất cho nh&agrave; đầu tư, cung cấp nguồn nh&acirc;n lực tốt, đồng thời cũng phải tự vươn l&ecirc;n để tho&aacute;t khỏi l&agrave; một tỉnh ngh&egrave;o&rdquo;, Thủ tướng n&oacute;i.</p>\r\n\r\n<p>Tại hội nghị, l&atilde;nh đạo tỉnh trao giấy chứng nhận đầu tư cho 18 dự &aacute;n với tổng mức đầu tư hơn 10.600 tỷ đồng. Trong số đ&oacute;, BIDV cũng thỏa thuận t&agrave;i trợ vốn với 5 dự &aacute;n, tương đương mức cam kết 5.120 tỷ đồng.</p>\r\n', 'public/images/20160418052533qt.jpg', 'B', 1, '2016-04-18', '18 dự án trên các lĩnh vực năng lượng, nông nghiệp công nghệ cao, du lịch… với tổng vốn hơn 10.600 tỷ đồng vừa được tỉnh Quảng Trị trao quyết định đầu tư.'),
(87, 'Du học sinh Pháp về Việt Nam làm hoa sen ướp tươi', '<p>Ước mơ l&agrave;m chủ được nhen nh&oacute;m từ khi c&ograve;n ngồi tr&ecirc;n ghế nh&agrave; trường n&ecirc;n sau khi du học 7 năm ở Ph&aacute;p, ch&agrave;ng thạc sĩ c&ocirc;ng nghệ h&oacute;a Ng&ocirc; Ch&iacute; C&ocirc;ng, sinh năm 1989 dấn th&acirc;n ngay v&agrave;o con đường kinh doanh. Ban đầu, C&ocirc;ng g&oacute;p vốn với một số người bạn c&ugrave;ng học b&ecirc;n Ph&aacute;p để mở một cửa h&agrave;ng b&aacute;n b&aacute;nh ngọt tại TP HCM, nhưng sau một năm hoạt động kh&ocirc;ng hiệu quả C&ocirc;ng quyết định chuyển nhượng lại v&agrave; trở về Đồng Th&aacute;p.</p>\r\n\r\n<p>&ldquo;Trong một lần du lịch Đ&agrave; Lạt, t&ocirc;i ph&aacute;t hiện ra tại đ&acirc;y c&oacute; b&aacute;n loại hoa hồng bất tử v&agrave; được biết nghệ nh&acirc;n Nguyễn C&ocirc;ng H&oacute;a l&agrave; người l&agrave;m ra những sản phẩm n&agrave;y n&ecirc;n t&igrave;m đến tham quan v&agrave; nảy ra &yacute; tưởng l&agrave;m sen ướp tươi với mong muốn giữ g&igrave;n v&agrave; ph&aacute;t triển h&igrave;nh ảnh hoa sen qu&ecirc; hương kh&ocirc;ng chỉ cho thị trường trong nước m&agrave; cả thế giới&rdquo;, C&ocirc;ng bộc bạch.</p>\r\n\r\n<p><img alt="" src="/public/images/images/imagenews/ccs.jpg" style="height:300px; width:500px" /></p>\r\n\r\n<p>Tới nay hoa sen ướp tươi của C&ocirc;ng đ&atilde; được b&aacute;n tại Đồng Th&aacute;p v&agrave; cửa h&agrave;ng tr&ecirc;n đường L&ecirc; Quang Định (quận B&igrave;nh Thạnh) TP HCM với số lượng 500-800 b&ocirc;ng mỗi th&aacute;ng với gi&aacute; 129.000 đồng một b&ocirc;ng. Ngo&agrave;i ra, C&ocirc;ng c&ograve;n kết hợp với một cửa h&agrave;ng b&aacute;n h&agrave;ng Việt ở Paris (Ph&aacute;p) trưng b&agrave;y v&agrave; b&aacute;n sản phẩm tại đ&acirc;y.</p>\r\n\r\n<p>Ngo&agrave;i việc l&agrave;m ra hoa sen ướp tươi th&igrave; C&ocirc;ng c&ograve;n kết hợp l&agrave;m b&igrave;nh gốm sử dụng c&ocirc;ng nghệ sơn nh&uacute;ng hydrographics, mỗi th&aacute;ng cho ra thị trường 300-400 sản phẩm với gi&aacute; 200.000 đến tr&ecirc;n một triệu đồng một b&igrave;nh.</p>\r\n\r\n<p>Mặc d&ugrave; đ&atilde; được thị trường biết đến nhiều hơn nhưng C&ocirc;ng cho biết chi ph&iacute; để l&agrave;m ra những sản phẩm n&agrave;y kh&aacute; cao n&ecirc;n tới nay m&ocirc; h&igrave;nh kinh doanh của ch&agrave;ng trai 8x n&agrave;y vẫn chưa thể h&ograve;a vốn. Tuy nhi&ecirc;n, lợi nhuận l&agrave;m ra cũng b&ugrave; đắp phần n&agrave;o cho chi ph&iacute; sản phẩm.</p>\r\n\r\n<p>Chia sẻ về kế hoạch trong năm nay, theo C&ocirc;ng, trước mắt sẽ ho&agrave;n thiện sản phẩm sen ướp, t&igrave;m c&aacute;ch giảm gi&aacute; th&agrave;nh xuống mức hợp l&yacute; nhất. T&igrave;m nguồn vốn hỗ trợ để x&acirc;y nh&agrave; xưởng, đồng thời t&igrave;m kiếm mở rộng thị trường sang một số nước phật gi&aacute;o, như Campuchia, Myanmar. ..</p>\r\n\r\n<p>Ngo&agrave;i ra, C&ocirc;ng c&ograve;n nghi&ecirc;n cứu một số loại hoa kh&ocirc; của Đồng Th&aacute;p như hoa hồng Sa Đ&eacute;c, c&aacute;t tường, hoa lan phục vụ thị trường trong nước v&agrave; hướng tới xuất khẩu.</p>\r\n', 'public/images/20160418052805hs.jpg', 'B', 1, '2016-04-18', 'Với ước mơ mang hoa sen đi khắp năm châu, chàng trai Ngô Chí Công (Đồng Tháp) quyết định gom hết số tiền dành dụm được khi du học ở Pháp để khởi nghiệp với hoa ướp tươi.'),
(88, 'Taekwondo Việt Nam thất bại tại vòng loại Olympic 2016', '<p>Sau hơn một th&aacute;ng tập huấn tại H&agrave;n Quốc, tuyển taekwondo Việt Nam h&agrave;nh qu&acirc;n sang Philippines dự V&ograve;ng loại Olympic 2016 khu vực ch&acirc;u &Aacute;. Mục ti&ecirc;u đội đề ra l&agrave; gi&agrave;nh từ một đến hai v&eacute; tới dự Thế vận hội v&agrave;o th&aacute;ng 8 tới. Tuy nhi&ecirc;n, kế hoạch tr&ecirc;n đ&atilde; đổ bể.</p>\r\n\r\n<p>Kim Tuyền lĩnh ấn ti&ecirc;n phong, thi đấu hạng c&acirc;n dưới 49 kg của nữ ng&agrave;y 16/4. Nhờ l&agrave; hạt giống số một, v&otilde; sĩ Việt Nam được v&agrave;o thẳng v&ograve;ng tứ kết. Tuy nhi&ecirc;n, sau khi đ&aacute;nh bại Zariya Kosimova (Uzbekista) 4-3, nh&agrave; v&ocirc; địch SEA Games 2015 thua đậm 7-13 trước Ainur Yesbergenova tại b&aacute;n kết, tan giấc mơ gi&agrave;nh v&eacute; dự Thế vận hội. Theo quy định của Li&ecirc;n đo&agrave;n taekwondo, chỉ c&oacute; hai v&otilde; sĩ đứng đầu mỗi hạng c&acirc;n mới gi&agrave;nh v&eacute; dự Olympic Rio 2016.</p>\r\n\r\n<p>Ng&agrave;y 17/4, ba th&agrave;nh vi&ecirc;n của tuyển Việt Nam bước v&agrave;o tranh t&agrave;i. Th&agrave;nh t&iacute;ch tốt nhất cũng chỉ l&agrave; tiến được tới b&aacute;n kết, thuộc về H&agrave; Thị Nguy&ecirc;n.</p>\r\n\r\n<p>Hạng c&acirc;n dưới 67 kg của nữ chỉ c&oacute; t&aacute;m v&otilde; sĩ tham dự. Nếu thắng được hai trận, H&agrave; Thị Nguy&ecirc;n sẽ c&oacute; v&eacute; dự Olympic. Ở trận ra qu&acirc;n, v&otilde; sĩ Việt Nam dễ d&agrave;ng đ&aacute;nh bại Gill Gaganjot (Ấn Độ) 7-0. Tuy nhi&ecirc;n, ở b&aacute;n kết, c&ocirc; đ&atilde; để Deniz Cansel (Kazakhstan) đ&aacute;nh bại ở hiệp phụ sau khi ho&agrave; 0-0 ở ba hiệp ch&iacute;nh.</p>\r\n\r\n<p>Phan Văn Duy, hạt giống số bốn ở hạng c&acirc;n dưới 58 kg của nam cũng để thua trong hiệp phụ, sau khi ho&agrave; 5-5 trước Almabrouk Hamad (Saudi Arabia) ở ba hiệp ch&iacute;nh ở trận ra qu&acirc;n. Trong khi đ&oacute;, Phan Trung Đức ở hạng dưới 68 kg của nam thua t&acirc;m phục khẩu phục 2-10 trước đối thủ được đ&aacute;nh gi&aacute; l&agrave; y&ecirc;u hơn Sila-on Peerathep (Th&aacute;i Lan).</p>\r\n\r\n<p>Tại Olympic 2012, taekwondo Việt Nam c&oacute; hai v&otilde; sĩ được tham dự l&agrave; L&ecirc; Huỳnh Ch&acirc;u v&agrave; Chu Ho&agrave;ng Diệu Linh.</p>\r\n', 'public/images/20160418052923tkd.jpg', 'SP', 1, '2016-04-18', 'Sau hai ngày tranh tài tại Philippines, bốn võ sĩ taekwondo Việt Nam không giành được tấm vé dự Olympic Rio 2016 nào.'),
(89, 'Torres ghi bàn bốn trận liên tiếp, Atletico san bằng số điểm với Barca', '<p>B&agrave;n thắng: Koke 15&#39;, Torres 59&#39;, Correa 83&#39;</p>\r\n\r\n<p>Tiếp đội đang xếp thứ tư từ dưới l&ecirc;n Granada, Atletico kh&ocirc;ng gặp nhiều kh&oacute; khăn trong h&agrave;nh tr&igrave;nh gi&agrave;nh ba điểm. Sau khi Koke mở tỷ số bằng c&uacute; dứt điểm từ cự ly gần ở ph&uacute;t 15, đến lượt Fernando Torres tỏa s&aacute;ng với một c&uacute; bấm b&oacute;ng qua thủ m&ocirc;n Andres Fernandez ở ph&uacute;t 59. Cuối trận đấu, tiền đạo trẻ Angel Corres c&oacute; được b&agrave;n thắng thứ tư cho ri&ecirc;ng m&igrave;nh tại La Liga, đồng thời ấn định chiến thắng 3-0.</p>\r\n\r\n<p>Việc gi&agrave;nh được ba điểm ở v&ograve;ng 33 La Liga l&agrave; một bước tiến v&ocirc; c&ugrave;ng quan trọng đối với Atletico. Sau chiến thắng n&agrave;y, Atletico kh&ocirc;ng những bảo vệ th&agrave;nh c&ocirc;ng vị tr&iacute; thứ hai so với v&ograve;ng trước, m&agrave; c&ograve;n san bằng c&aacute;ch biệt về điểm với đội dẫn đầu Barca. Hai đội hiện c&ugrave;ng c&oacute; 76 điểm sau 33 v&ograve;ng, trong khi Barca vẫn chiếm vị tr&iacute; số một nhờ hơn đối đầu trực tiếp.</p>\r\n\r\n<p>Ở v&ograve;ng 33, Real v&agrave; Atletico c&ugrave;ng gi&agrave;nh chiến thắng. Đ&acirc;y đều l&agrave; bước tiến lớn của hai đội b&oacute;ng th&agrave;nh Madrid, trong khi Barca tiếp tục chuỗi ng&agrave;y tồi tệ khi để thua Valencia với tỷ số 1-2. Với việc hai đội dẫn đầu c&oacute; c&ugrave;ng 76 điểm v&agrave; Real c&oacute; 75 điểm, cuộc đua tới chức v&ocirc; địch La Liga sẽ trở n&ecirc;n thập phần gay cấn trong năm v&ograve;ng cuối.</p>\r\n\r\n<p>Trở lại với chiến thắng của Atletico. Sau một trận kh&ocirc;ng được v&agrave;o s&acirc;n do &aacute;n treo gi&ograve; (tứ kết lượt về Champions League), Torres vẫn thể hiện phong độ tốt. Tiền đạo n&agrave;y vừa c&oacute; c&ocirc;ng kiến tạo b&agrave;n mở tỷ số (dứt điểm ở ph&uacute;t 15 dẫn đến c&uacute; đệm b&oacute;ng cận th&agrave;nh của Koke), vừa ghi t&ecirc;n v&agrave;o bảng điện tử bằng c&uacute; dứt điểm ch&iacute;nh x&aacute;c nh&acirc;n đ&ocirc;i c&aacute;ch biệt. Đ&acirc;y l&agrave; trận thứ tư li&ecirc;n tiếp Torres ghi b&agrave;n cho Atletico, bao gồm ba tại La Liga v&agrave; một ở tứ kết lượt đi Champions League.</p>\r\n\r\n<p>So với trận tứ kết lượt về Champions League giữa tuần trước (thắng Barca với tỷ số 2-0), Atletico kh&ocirc;ng hề cho thấy dấu hiệu tự m&atilde;n. C&aacute;c cầu thủ của HLV Diego Simeone vẫn thi đấu tập trung với mục ti&ecirc;u gi&agrave;nh danh hiệu trong m&ugrave;a giải 2015-2016. B&ecirc;n cạnh ba cầu thủ lập c&ocirc;ng, Atletico c&ograve;n c&oacute; một gương mặt nổi bật l&agrave; tiền vệ trẻ Saul Niguez. Giống như trận tứ kết lượt về Champions League, Niguez tỏ ra cơ động ở tuyến giữa, đồng thời đ&oacute;ng g&oacute;p một đường chuyền dọn cỗ. Lần n&agrave;y người tận dụng th&agrave;nh c&ocirc;ng cơ hội l&agrave; Correa với pha dứt điểm ch&igrave;m đ&aacute;nh bại thủ m&ocirc;n Fernandez.</p>\r\n', 'public/images/20160418053149torres.jpg', 'SF', 1, '2016-04-18', 'Atletico Madrid vừa tiến thêm một bước quan trọng trong cuộc đua giành chức vô địch La Liga khi đánh bại Granada với tỷ số 3-0.'),
(90, 'Messi ghi bàn thứ 500 trong ngày buồn của Barca', '<p>Tối 17/4, Barca để thua Valencia với tỷ số 1-2 tr&ecirc;n s&acirc;n nh&agrave; Nou Camp. Với CĐV xứ Catalonia, kết quả n&agrave;y chẳng c&oacute; g&igrave; đ&aacute;ng n&oacute;i ngoại trừ b&agrave;n thắng của Lionel Messi. Đ&acirc;y l&agrave; b&agrave;n thứ 500 tr&ograve;n của M10 trong sự nghiệp cầu thủ bắt đầu từ năm 2004.</p>\r\n\r\n<p>T&iacute;nh trong m&agrave;u &aacute;o Barca, Messi đ&atilde; c&oacute; 450 b&agrave;n từ 525 trận. 50 b&agrave;n c&ograve;n lại được cầu thủ n&agrave;y ghi từ 107 trận chơi cho đội tuyển Argentina.<img alt="" src="/public/images/images/imagenews/m10.jpg" style="height:361px; width:500px" /></p>\r\n\r\n<p>Messi ghi b&agrave;n đầu ti&ecirc;n v&agrave;o th&aacute;ng 5/2005, với pha dứt điểm trong trận Barca - Albacete. Khi đ&oacute; cầu thủ n&agrave;y mới 17 tuổi. Gần một năm sau đ&oacute;, Messi c&oacute; b&agrave;n &quot;mở h&agrave;ng&quot; trong m&agrave;o &aacute;o tuyển Argentina khi chơi trận giao hữu với Croatia.</p>\r\n\r\n<p>Với 450 b&agrave;n cho Barca, Messi hiện l&agrave; cầu thủ lập c&ocirc;ng nhiều nhất trong lịch sử CLB. Trong khi đ&oacute;, 50 b&agrave;n với tuyển Argentina gi&uacute;p M10 xếp thứ hai, chỉ k&eacute;m s&aacute;u b&agrave;n so với kỷ lục gia Gabriel Batistuta.</p>\r\n\r\n<p>So với Cristiano Ronaldo, người đạt tới dấu mốc 500 b&agrave;n hồi th&aacute;ng 9/2015, Messi hiện k&eacute;m 39 b&agrave;n. CR7 mới c&oacute; b&agrave;n thứ 539 trong trận Real thắng Getafe với tỷ số 5-1 h&ocirc;m 16/4.</p>\r\n', 'public/images/20160418053447si.jpg', 'SF', 1, '2016-04-18', 'M10 đạt được một dấu mốc quan trọng trong sự nghiệp cầu thủ đúng vào lúc Barca phải chịu thất bại thứ ba liên tiếp trên đấu trường La Liga.'),
(91, 'Nadal lần thứ chín vô địch Monte Carlo', '<p>Rafael Nadal ấn định thắng lợi 7-5, 5-7, 6-0 sau hai tiếng 45 ph&uacute;t đọ sức.</p>\r\n\r\n<p>Lần thứ ch&iacute;n trong sự nghiệp tay vợt người T&acirc;y Ban Nha đăng quang tại giải Masters 1000 đầu ti&ecirc;n của năm tr&ecirc;n s&acirc;n đất nện. Trước đ&oacute;, anh c&oacute; t&aacute;m danh hiệu li&ecirc;n tiếp ở Monte Carlo, nhưng đứt mạch chiến thắng tại đ&acirc;y kể từ 2012.</p>\r\n\r\n<p>Với danh hiệu Masters 1000 đầu ti&ecirc;n kể từ sau Madrid 2014, Nadal san bằng th&agrave;nh t&iacute;ch 28 chức v&ocirc; địch Masters 1000 với số một thế giới Novak Djokovic &ndash; tay vợt sớm bị loại ngay ở v&ograve;ng hai của giải lần n&agrave;y.</p>\r\n\r\n<p>Trận chung kết diễn ra căng thẳng v&agrave; k&eacute;o d&agrave;i v&igrave; c&oacute; tới 13 break giữa hai tay vợt. Nadal gi&agrave;nh được tới t&aacute;m break, trong tổng số 21 cơ hội. Trong khi đ&oacute;, Gael Monfils cũng c&oacute; được năm break từ 13 cơ hội. Nếu tay vợt số 16 thế giới kh&ocirc;ng đột ngột chơi đuối tay ở set quyết định, cuộc đấu c&oacute; lẽ sẽ c&ograve;n giằng co quyết liệt đến ph&uacute;t ch&oacute;t.</p>\r\n\r\n<p>Nadal đ&atilde; bỏ lỡ cơ hội kết th&uacute;c set thứ nhất sớm hơn, v&agrave; li&ecirc;n tục l&atilde;ng ph&iacute; lợi thế break ở set thứ hai. V&igrave; vậy, đ&acirc;y kh&ocirc;ng thể coi l&agrave; m&agrave;n tr&igrave;nh diễn ấn tượng của anh tr&ecirc;n mặt s&acirc;n sở trường. Nadal chỉ c&oacute; một c&uacute; giao b&oacute;ng ăn điểm trực tiếp trong cả trận, so với bốn của đối phương. Nhưng b&ugrave; lại, tay vợt T&acirc;y Ban Nha mắc &iacute;t lỗi tự đ&aacute;nh hỏng hơn đối thủ người Ph&aacute;p. Tỷ lệ ăn điểm giao b&oacute;ng lần một của Nadal cũng vượt trội so với đối phương. Như tất cả c&aacute;c trận đấu trước đ&oacute; tại giải lần n&agrave;y, Nadal ở chung kết cũng gi&agrave;nh được nhiều điểm hơn đối thủ trong c&aacute;c pha l&ecirc;n lưới tấn c&ocirc;ng.</p>\r\n\r\n<p>Ri&ecirc;ng set thứ nhất đ&atilde; diễn ra trong hơn một tiếng. Nadal dẫn trước 5-3, nhưng kh&ocirc;ng thể sớm kh&eacute;p lại set đầu khi bị đ&ograve;i lại break ở game thứ ch&iacute;n. Ở game thứ mười, tay vợt T&acirc;y Ban Nha đ&atilde; c&oacute; c&oacute; hội đoạt th&ecirc;m break nhưng kh&ocirc;ng tận dụng được. Sau khi bị san h&ograve;a 5-5, Nadal vượt l&ecirc;n dẫn 6-5 khi giao b&oacute;ng, v&agrave; tiếp đ&oacute; gi&agrave;nh được break then chốt ở game thứ 12 để ấn định tỷ số thắng 7-5.</p>\r\n\r\n<p>Set thứ hai chứng kiến hai tay vợt thay nhau bẻ game đối phương giao b&oacute;ng. Nadal sớm mất game giao b&oacute;ng v&agrave; bỏ lỡ thời cơ đ&ograve;i break ở game thứ tư, qua đ&oacute; bị dẫn trước 1-3. Anh sau đ&oacute; cũng đ&ograve;i lại được break để san h&ograve;a 3-3. Nhưng ngay ở game sau anh lại rơi v&agrave;o thế bất lợi khi để Monfils c&oacute; th&ecirc;m break nữa. Một set của những break li&ecirc;n tiếp chưa dừng lại ở đ&oacute;. Tới lượt Nadal c&oacute; th&ecirc;m break để gỡ h&ograve;a 4-4. Nhưng sau khi h&ograve;a 5-5, Nadal lại để đối phương gi&agrave;nh th&ecirc;m break ở game thứ 11. Với lợi thế đ&oacute;, Monfils ấn định kết quả 7-5 khi cầm ph&aacute;t b&oacute;ng.</p>\r\n\r\n<p>Những g&igrave; diễn ra ở hai set đầu, đặc biệt l&agrave; từ những game cuối set hai, b&aacute;o hiệu một set quyết định c&oacute; thể diễn ra rất quyết liệt. Nhưng Nadal sớm bẻ được game đối phương ph&aacute;t b&oacute;ng v&agrave; dễ d&agrave;ng thắng một mạch ở set thứ ba tới tỷ số 6-0, khi Monfils gần như kh&ocirc;ng c&ograve;n sức kh&aacute;ng cự.</p>\r\n\r\n<p>&ldquo;T&ocirc;i đ&atilde; trải qua một m&ugrave;a giải kh&oacute; khăn hồi năm ngo&aacute;i, nhưng t&ocirc;i đ&atilde; c&oacute; sự chuẩn bị tốt hơn cho năm nay. Thật tuyệt vời khi lại gi&agrave;nh được danh hiệu Masters 1000. T&ocirc;i hy vọng tuần thi đấu vừa xong sẽ gi&uacute;p t&ocirc;i nhiều để chơi tốt hơn thời gian tới&rdquo;, Nadal ph&aacute;t biểu sau chiến thắng.</p>\r\n', 'public/images/20160418060202lovenadal.jpg', 'SP', 1, '2016-04-18', 'Trong lần thứ 10 vào chung kết giải Masters 1000 sân đất nện, tay vợt số năm thế giới phải rất vất vả mới giành được chiến thắng ba set khi chạm trán hạt giống số 13 Gael Monfils.'),
(92, 'Tiến Minh thắng chóng vánh, vào chung kết tại Phần Lan', '<p>S&aacute;u tiếng sau khi lội ngược d&ograve;ng đ&aacute;nh bại tay vợt &iacute;t hơn ch&iacute;n tuổi Rasmus Fladberg, Tiến Minh bước v&agrave;o trận b&aacute;n kết. Tuy nhi&ecirc;n, lần n&agrave;y tay vợt số một Việt Nam kh&ocirc;ng thất thế về mặt thể lực bởi đối thủ Kestutis Navickas cũng đ&atilde; bước sang tuổi 32 v&agrave; mới trải qua ba set vất vả ở tứ kết. Với tr&igrave;nh độ v&agrave; kinh nghiệm vượt trội, Tiến Minh chỉ mất hai set đấu để kết liễu đối thủ k&eacute;m anh 70 bậc.</p>\r\n\r\n<p>Trong set một, ngay sau khi để Navickas vươn l&ecirc;n dẫn 2-1, Tiến Minh c&oacute; chuỗi ăn ch&iacute;n điểm li&ecirc;n tiếp. C&oacute; thời điểm Navickas, thứ 106 thế giới, thu hẹp khoảng c&aacute;ch xuống chỉ c&ograve;n ba điểm nhưng Tiến Minh c&oacute; hai chuỗi ăn năm v&agrave; s&aacute;u điểm để kết th&uacute;c set đấu với c&aacute;ch biệt lớn 21-9.</p>\r\n\r\n<p>Set đấu thứ hai khởi đầu kịch t&iacute;nh hơn. Tiến Minh dẫn trước 4-0 rồi để đối thủ gỡ ho&agrave; 4-4. Tuy nhi&ecirc;n, hy vọng của tay vợt đến từ Lithuania nhanh ch&oacute;ng bị dập tắt khi hạt giống số một của giải c&oacute; chuỗi ăn năm điểm li&ecirc;n tiếp. Kể từ đ&oacute;, Tiến Minh kh&ocirc;ng mắc sai s&oacute;t n&agrave;o, li&ecirc;n tục dẫn điểm v&agrave; sớm chiến thắng với c&aacute;ch biệt l&ecirc;n tới ch&iacute;n điểm (21-12).</p>\r\n\r\n<p>Thắng ch&oacute;ng v&aacute;nh, Tiến Minh gi&agrave;nh quyền v&agrave;o chung kết giải Phần Lan mở rộng. Đối thủ của anh trong trận đấu cuối c&ugrave;ng của giải l&agrave; Kanta Tsuneyama. Tay vợt người Nhật Bản năm nay 19 tuổi v&agrave; đang đứng thứ 90 thế giới. Trong lần đụng độ v&agrave;o th&aacute;ng 7/2015 tr&ecirc;n đất Mỹ, Tiến Minh đ&atilde; để thua ch&oacute;ng v&aacute;nh 0-2 trước Tsuneyama (13-21, 12-21).</p>\r\n\r\n<p>Giải Phần Lan Mở rộng diễn ra từ ng&agrave;y 7/4 tới ng&agrave;y 10/4 tại th&agrave;nh phố Vantaa, c&oacute; tổng giải thưởng l&agrave; 17.500 đ&ocirc;la. Đ&acirc;y l&agrave; giải đấu c&oacute; tr&igrave;nh độ thấp, n&ecirc;n Tiến Minh, d&ugrave; đứng thứ 36 thế giới, vẫn l&agrave; hạt giống số một ở nội dung đơn nam.</p>\r\n', 'public/images/20160418061133tm.jpg', 'SB', 1, '2016-04-18', 'Tay vợt số một Việt Nam, hạt giống số một của giải, chỉ mất 27 phút để hạ Kestutis Navickas, giành vé vào chơi trận cuối cùng của giải Phần Lan Mở rộng 2016.'),
(93, 'Hai VĐV điền kinh Nga dọa kiện để tránh án cấm thi đấu vĩnh viễn', '<p>Kristina Ugarova (VĐV chạy 1500m) v&agrave; Tatyana Myazina (chạy 800m) l&agrave; hai trong số năm VĐV điền kinh Nga bị đề nghị cấm thi đấu vĩnh viễn trong b&aacute;o c&aacute;o được WADA c&ocirc;ng bố tuần trước.</p>\r\n\r\n<p>Ugarova m&ocirc; tả c&aacute;c c&aacute;o buộc sử dụng doping của WADA l&agrave; &quot;vu khống&quot;. Trong khi đ&oacute; Myazina n&oacute;i: &ldquo;T&ocirc;i kh&ocirc;ng d&ugrave;ng bất kỳ chất k&iacute;ch th&iacute;ch bị cấm n&agrave;o&quot;.</p>\r\n\r\n<p>Hiện tại cả Li&ecirc;n đo&agrave;n điền kinh Nga bị cấm tham gia v&ocirc; thời hạn tại mọi giải đấu quốc tế. Theo đ&oacute;, c&aacute;c VĐV điền kinh Nga sẽ kh&ocirc;ng được tranh t&agrave;i ở mọi giải đấu từ nay cho tới khi c&oacute; quyết định ch&iacute;nh thức, c&oacute; thể bao gồm World Athletic Series v&agrave; cả Olympic 2016 - Thế vận hội m&ugrave;a h&egrave; khai mạc v&agrave;o ng&agrave;y 5/8 năm tới, ở Rio de Janeiro, Brazil.</p>\r\n\r\n<p>C&aacute;c thanh tra của Li&ecirc;n đo&agrave;n điền kinh quốc tế (IAAF) sẽ bắt đầu c&ocirc;ng việc kiểm tra cải c&aacute;ch hệ thống kiểm so&aacute;t doping tại Nga v&agrave;o đầu năm 2016 để quyết định xem liệu điền kinh Nga c&oacute; được phục hồi tư c&aacute;ch tham gia c&aacute;c giải đấu.</p>\r\n\r\n<p>WADA mới đ&acirc;y đ&atilde; c&ocirc;ng bố b&aacute;o c&aacute;o hơn 300 trang về c&aacute;c c&aacute;o buộc sử dụng doping, che giấu x&eacute;t nghiệm doping v&agrave; nhận hối lộ để hủy kết quả kiểm tra doping trong điền kinh Nga. Ủy ban độc lập của Cơ quan n&agrave;y từ th&aacute;ng 12/2014 đ&atilde; tiến h&agrave;nh c&aacute;c cuộc điều tra vấn nạn doping hệ thống tr&agrave;n lan trong thể thao Nga. Ủy ban n&agrave;y c&oacute; sự tham gia của cả một cảnh s&aacute;t Interpol kỳ cựu, người từng tham gia vụ lật mặt Lance Armstrong.</p>\r\n\r\n<p>WADA chỉ c&oacute; thể c&aacute;o buộc v&agrave; c&ocirc;ng bố c&aacute;c trường hợp vi phạm quy định ph&ograve;ng chống doping theo chuẩn quốc tế. Cơ quan n&agrave;y kh&ocirc;ng c&oacute; quyền xử phạt c&aacute;c c&aacute; nh&acirc;n VĐV cũng như c&aacute;c li&ecirc;n đo&agrave;n thể thao. Quyền hạn đ&oacute; hiện nay thuộc về c&aacute;c li&ecirc;n đo&agrave;n thể thao quốc tế v&agrave; Ủy ban Olympic quốc tế (IOC).</p>\r\n\r\n<p>Chủ tịch IOC Thomas Bach đề nghị trong tương lai tất cả những c&aacute;o buộc doping sẽ do T&ograve;a &aacute;n Trọng t&agrave;i Thể thao (CAS) tiến h&agrave;nh x&eacute;t xử v&agrave; đưa ra h&igrave;nh thức xử phạt. CAS hoan ngh&ecirc;nh &yacute; tưởng đ&oacute;, nhưng Tổng thư k&yacute; Matthieu Reeb cho biết vẫn c&ograve;n nhiều bước phải trải qua trước khi c&aacute;c b&ecirc;n li&ecirc;n quan thống nhất việc n&agrave;y.</p>\r\n', 'public/images/20160418063208dk.jpg', 'SR', 1, '2016-04-18', 'Hai VĐV chạy cự ly trung bình của Nga tuyên bố sẽ có hành động pháp lý để khẳng định sự vô tội, nếu họ bị cấm thi đấu suốt đời theo đề nghị của WADA (Cơ quan chống doping thế giới).');

